import React, { useEffect, useState } from "react";
import { Link, useLocation } from "react-router-dom";
import "./Header.css";

export default function Header() {
  const location = useLocation();
  const [balance, setBalance] = useState(0);

  // atualiza saldo sempre que ele mudar no localStorage
  useEffect(() => {
    const interval = setInterval(() => {
      const b = Number(localStorage.getItem("balance") || 0);
      setBalance(b);
    }, 400);
    return () => clearInterval(interval);
  }, []);

  return (
    <header className="header-container">
      <div className="header-content">

        {/* LOGO */}
        <div className="header-logo">
          Alef & Larissa <span>BET</span>
        </div>

        {/* MENU CENTRAL */}
        <nav className="header-menu">
          <Link
            className={location.pathname === "/" ? "active" : ""}
            to="/"
          >
            InÃ­cio
          </Link>
          <Link
            className={location.pathname === "/apostas" ? "active" : ""}
            to="/apostas"
          >
            Apostas
          </Link>
          <Link
            className={location.pathname === "/conta" ? "active" : ""}
            to="/conta"
          >
            Conta
          </Link>
        </nav>

        {/* SALDO + USER */}
        <div className="header-right">
          <div className="header-balance">
            R$ {balance.toFixed(2)}
          </div>

          <div className="header-user-icon">
            <span>ðŸ‘¤</span>
          </div>
        </div>
      </div>
    </header>
  );
}
