// src/components/BetSlipSidebar.jsx

import React, { useState } from "react";
import api from "../api/api";
import showToast from "../utils/showToast";
import "../styles/betslip.css";

export default function BetSlipSidebar({ slip, setSlip, isOpen, onClose }) {
  const [loading, setLoading] = useState(false);

  const handleConfirmBet = async () => {
    if (!slip || !slip.coupleName || !slip.prediction) {
      return showToast("Preencha todos os campos!", "error");
    }

    try {
      setLoading(true);
      const res = await api.post("/bets/create", slip);

      showToast("Aposta registrada com sucesso!", "success");

      setSlip(null);
      onClose();
    } catch (error) {
      showToast("Erro ao registrar aposta", "error");
    } finally {
      setLoading(false);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="betslip-sidebar">
      <div className="betslip-header">
        <h2>Seu Slip</h2>
        <button className="close-btn" onClick={onClose}>×</button>
      </div>

      {!slip ? (
        <p className="empty-slip">Nenhuma aposta selecionada.</p>
      ) : (
        <div className="betslip-content">
          <div className="betslip-item">
            <label>Nome do Casal:</label>
            <input
              type="text"
              value={slip.coupleName}
              onChange={(e) => setSlip({ ...slip, coupleName: e.target.value })}
              placeholder="Ex: João & Maria"
            />
          </div>

          <div className="betslip-item">
            <label>Tipo de Término:</label>
            <input
              type="text"
              value={slip.prediction}
              onChange={(e) => setSlip({ ...slip, prediction: e.target.value })}
              placeholder="Ex: traição, desinteresse..."
            />
          </div>

          <div className="betslip-item">
            <label>Tempo estimado:</label>
            <input
              type="text"
              value={slip.time}
              onChange={(e) => setSlip({ ...slip, time: e.target.value })}
              placeholder="Ex: 2 semanas"
            />
          </div>

          <button
            className="confirm-btn"
            onClick={handleConfirmBet}
            disabled={loading}
          >
            {loading ? "Enviando..." : "Confirmar Aposta"}
          </button>
        </div>
      )}
    </div>
  );
}
