import React, { useEffect, useState } from "react";
import "../styles/Dashboard.css";
import showToast from "../utils/showToast";

export default function Dashboard({ slip, setSlip }) {

  // Card animado do casal
  const casal = {
    nomeA: "Alef",
    nomeB: "Larissa",
    fotoA: "/img/alef.jpg",
    fotoB: "/img/larissa.jpg",
  };

  const apostasAlta = [
    { id: 1, motivo: "Falta de comunica칞칚o", odd: 1.85 },
    { id: 2, motivo: "Trai칞칚o / quebra de confian칞a", odd: 2.40 },
    { id: 3, motivo: "Diferen칞as de objetivos", odd: 3.10 },
    { id: 4, motivo: "Conflitos frequentes", odd: 2.75 },
    { id: 5, motivo: "Desinteresse / esfriamento", odd: 2.20 },
  ];

  // Anima칞칚o r치pido no card
  const animateCard = (id) => {
    const el = document.getElementById(`dash-card-${id}`);
    if (!el) return;

    el.classList.add("clicked");
    setTimeout(() => el.classList.remove("clicked"), 250);
  };

  const addToSlip = (item) => {
    animateCard(item.id);

    setSlip((prev) => [
      ...prev,
      {
        betId: item.id,
        betTitle: "Aposta R치pida do Dashboard",
        optionId: item.id,
        label: item.motivo,
        odds: item.odd,
        amount: 0,
      },
    ]);

    showToast("Adicionado ao bilhete!", "success");
  };

  return (
    <div className="dashboard fade-in">

      {/* CARD DO CASAL */}
      <div className="casal-card">
        <div className="casal-imgs">
          <img src="/img/1" alt="Alef" />
          <img src={casal.fotoB} />
        </div>
        <h2>{casal.nomeA} 仇벒잺 {casal.nomeB}</h2>
      </div>

      <h2 className="sec-title">游댠 Apostas em Alta</h2>

      <div className="apostas-grid">
        {apostasAlta.map((item) => (
          <div
            key={item.id}
            id={`dash-card-${item.id}`}
            className="aposta-card"
            onClick={() => addToSlip(item)}
          >
            <p className="aposta-text">{item.motivo}</p>
            <span className="aposta-odd">{item.odd}</span>
          </div>
        ))}
      </div>
    </div>
  );
}
