// frontend/src/components/BetSlip.jsx
import React from "react";
import api from "../api/api";
import showToast from "../utils/showToast";
import "../styles/betslip.css";

export default function BetSlip({ slip, setSlip, onRefresh }) {
  // alterar amount de uma seleção (índice)
  const changeAmount = (idx, value) => {
    const clone = [...slip];
    clone[idx] = { ...clone[idx], amount: Number(value) };
    setSlip(clone);
  };

  const removeItem = (idx) => {
    const clone = [...slip];
    clone.splice(idx, 1);
    setSlip(clone);
  };

  // calcular retorno por linha e total
  const lineReturn = (item) => {
    const a = Number(item.amount || 0);
    const o = Number(item.odds || 0);
    return a * o;
  };
  const totalReturn = slip.reduce((s, it) => s + lineReturn(it), 0);

  // apostar um item (usa API)
  const placeSingle = async (idx) => {
    const item = slip[idx];
    if (!item) return;
    if (!item.amount || item.amount <= 0) {
      showToast("Informe um valor válido para apostar.", "error");
      return;
    }
    if (!item.betId) {
      showToast("Aposta estática — esta opção não está vinculada a uma bet real.", "error");
      return;
    }

    try {
      const token = localStorage.getItem("token");
      const res = await api.post(
        `/bets/${item.betId}/wager`,
        { optionId: item.optionId, amount: Number(item.amount) },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      showToast("Aposta registrada!", "success");

      // atualizar balance local se backend retornou newBalance
      if (res.data?.newBalance !== undefined) {
        localStorage.setItem("balance", res.data.newBalance);
      }

      // remover item do slip
      removeItem(idx);

      // atualiza dados (saldo, bets, etc.)
      if (onRefresh) onRefresh();
    } catch (err) {
      const status = err.response?.status;
      const msg = err.response?.data?.message || "Erro ao apostar.";
      if (status === 402) {
        showToast("Saldo insuficiente. Deposite para continuar.", "error");
      } else {
        showToast(msg, "error");
      }
    }
  };

  // apostar todas as seleções (faz posts sequenciais)
  const placeAll = async () => {
    if (slip.length === 0) return showToast("Sem seleções no slip.", "error");

    // clona para iterar sem ser afetado pela remoção
    for (let i = 0; i < slip.length; i++) {
      // sempre tenta apostar o primeiro item da lista atualizada
      try {
        await placeSingle(0);
      } catch (e) {
        // placeSingle já trata erros com toast
        break;
      }
    }
  };

  return (
    <div className="betslip">
      <h4>Bilhete</h4>

      {slip.length === 0 && <div className="small">Nenhuma seleção</div>}

      {slip.map((s, idx) => (
        <div className="betslip-line" key={`${s.betId ?? "static"}-${s.optionId}-${idx}`}>
          <div className="left">
            <div className="label">{s.label}</div>
            <div className="meta">{s.betTitle}</div>
          </div>

          <div className="center">
            <input
              type="number"
              min="0"
              placeholder="Valor"
              value={s.amount || ""}
              onChange={(e) => changeAmount(idx, e.target.value)}
            />
            <div className="retorno">Retorno: R$ {lineReturn(s).toFixed(2)}</div>
          </div>

          <div className="right">
            <div className="odd">{Number(s.odds).toFixed(2)}</div>
            <button className="btn small" onClick={() => placeSingle(idx)}>Apostar</button>
            <button className="btn ghost small" onClick={() => removeItem(idx)}>Remover</button>
          </div>
        </div>
      ))}

      <div className="betslip-footer">
        <div className="total">Possível retorno total: R$ {totalReturn.toFixed(2)}</div>
        <div className="actions">
          <button className="btn" onClick={placeAll} disabled={slip.length === 0}>Apostar tudo</button>
        </div>
      </div>
    </div>
  );
}
