// src/App.jsx
import React, { useEffect, useState } from "react";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Dashboard from "./pages/Dashboard";
import Conta from "./pages/Conta";
import Deposito from "./pages/Deposito";
import Saque from "./pages/Saque";
import Historico from "./pages/Historico";
import Patrocinadores from "./pages/Patrocinadores";

export default function App() {
  const [page, setPage] = useState("login");
  const [user, setUser] = useState(null);

  useEffect(() => {
    const saved = localStorage.getItem("user");
    const token = localStorage.getItem("token");
    if (saved && token) {
      setUser(JSON.parse(saved));
      setPage("dashboard");
    }
  }, []);

  function handleLogin(userData) {
    setUser(userData);
    localStorage.setItem("user", JSON.stringify(userData));
    setPage("dashboard");
  }

  function handleLogout() {
    localStorage.removeItem("user");
    localStorage.removeItem("token");
    setUser(null);
    setPage("login");
  }

  // navigation handler used by pages
  function navigate(to) {
    setPage(to);
  }

  return (
    <>
      {page === "login" && <Login onNavigate={navigate} onLogin={handleLogin} />}
      {page === "register" && <Register onNavigate={navigate} />}
      {page === "dashboard" && user && (
        <Dashboard user={user} onNavigate={navigate} onLogout={handleLogout} />
      )}
      {page === "conta" && user && <Conta user={user} onNavigate={navigate} />}
      {page === "deposito" && user && <Deposito user={user} onNavigate={navigate} />}
      {page === "saque" && user && <Saque user={user} onNavigate={navigate} />}
      {page === "historico" && user && <Historico user={user} onNavigate={navigate} />}
      {page === "patrocinadores" && <Patrocinadores onNavigate={navigate} />}
    </>
  );
}
