import React, { useEffect, useState } from "react";
import "../styles/Dashboard.css";
import showToast from "../utils/showToast";
import BetSlipSidebar from "../components/BetSlipSidebar";

export default function Dashboard() {
  const [saldo, setSaldo] = useState(0);
  const [slip, setSlip] = useState([]);

  useEffect(() => {
    const fakeBalance = 125.40;
    setSaldo(fakeBalance);
  }, []);

  // Lista de apostas r치pidas que j치 podem ser selecionadas
  const apostasAlta = [
    { id: 1, motivo: "Falta de comunica칞칚o", odd: 1.85 },
    { id: 2, motivo: "Trai칞칚o / quebra de confian칞a", odd: 2.40 },
    { id: 3, motivo: "Diferen칞as de objetivos", odd: 3.10 },
    { id: 4, motivo: "Conflitos frequentes", odd: 2.75 },
    { id: 5, motivo: "Desinteresse / esfriamento", odd: 2.20 },
  ];

  const addToSlip = (item) => {
    setSlip((prev) => [
      ...prev,
      {
        betId: item.id,
        betTitle: "Aposta r치pida (Dashboard)",
        optionId: item.id,
        label: item.motivo,
        odds: item.odd,
        amount: 0,
      },
    ]);

    showToast("Adicionado ao bilhete!", "success");
  };

  return (
    <div className="dashboard fade-in">

      <h2 className="sec-title">游댠 Apostas em Alta</h2>

      <div className="apostas-grid">
        {apostasAlta.map((item) => (
          <div key={item.id} className="aposta-card" onClick={() => addToSlip(item)}>
            <p className="aposta-text">{item.motivo}</p>
            <span className="aposta-odd">Odd {item.odd}</span>
          </div>
        ))}
      </div>

      {/* BetSlip global funcionando */}
      <BetSlipSidebar slip={slip} setSlip={setSlip} />
    </div>
  );
}
