// src/pages/Dashboard.jsx
import React, { useState, useEffect } from "react";
import "../styles/Dashboard.css";
import betsData from "../data/betsData";
import CouplesSelector from "../components/CouplesSelector";
import OtherBetsBox from "../components/OtherBetsBox";

export default function Dashboard({ user = {}, onNavigate }) {
  const [selectedCouple, setSelectedCouple] = useState(betsData.couples[0].id);
  const [slip, setSlip] = useState([]);
  const [theme, setTheme] = useState(() => localStorage.getItem("theme") || "light");

  useEffect(() => {
    document.body.classList.toggle("dark", theme === "dark");
    localStorage.setItem("theme", theme);
  }, [theme]);

  // --- Betslip handlers ---
  function addToSlip(item, labelPrefix = "Principal") {
    setSlip((p) => {
      if (p.find(x => x.id === item.id)) return p;
      return [...p, { id: item.id, label: item.label, odds: item.odds, oddLabel: labelPrefix, amount: 0 }];
    });
  }

  function changeAmt(i, v) {
    setSlip(p => {
      const c = [...p]; c[i] = {...c[i], amount: Number(v) || 0 }; return c;
    });
  }

  function remove(i) {
    setSlip(p => p.filter((_, idx) => idx !== i));
  }

  function place(i) {
    const item = slip[i];
    if (!item || !item.amount || item.amount <= 0) return alert("Valor inv√°lido");
    alert(`Apostado ${item.amount} pts em ${item.label} ‚Äî retorno ${(item.amount * item.odds).toFixed(2)}`);
    setSlip(p => p.filter((_, idx) => idx !== i));
  }

  // logout: clear local storage and navigate to login
  function handleLogout() {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    // if you want to clear theme leave it; keep theme for UX
    onNavigate("login");
  }

  // fake trends
  const trends = betsData.mainBets.slice(0,6).map((b, idx) => ({
    label: b.label,
    score: Math.max(50 - idx * 6, 12)
  }));

  return (
    <div className="app-wrapper">
      <div className="dashboard-container">

        {/* TOPBAR */}
        <div className="topbar">
          <div className="left">
            <div className="brand" onClick={() => onNavigate("inicio")}>
              <span className="logo-emoji">üéØ</span>
              <span>BET DO T√âRMINO</span>
            </div>

            <CouplesSelector couples={betsData.couples} value={selectedCouple} onChange={setSelectedCouple} />
          </div>

          <div className="actions">
            <div className="balance">üíé Saldo: <strong>{user.balance || 0} pts</strong></div>

            {/* theme toggle */}
            <button
              className="icon-btn"
              title="Alternar tema"
              onClick={() => setTheme(prev => prev === "dark" ? "light" : "dark")}
            >
              {theme === "dark" ? "‚òÄÔ∏è Light" : "üåô Dark"}
            </button>

            {/* logout */}
            <button
              className="icon-btn"
              title="Sair"
              onClick={handleLogout}
            >
              üö™ Sair
            </button>
          </div>
        </div>

        {/* MAIN GRID */}
        <div className="main-grid">
          {/* SIDEBAR */}
          <div className="sidebar card">
            <div className="nav">
              <div className="nav-item active">üè† In√≠cio</div>
              <div className="nav-item">üé≤ Apostas</div>
              <div className="nav-item">üìà Resultados</div>
              <div className="nav-item">üî• Tend√™ncias</div>
              <div className="nav-item">üìú Hist√≥rico</div>
            </div>

            <div style={{height:12}} />

            <div className="widget-small card">
              <div className="label">‚ù§Ô∏è Casais em alta</div>
              <div className="value">3 casais</div>
            </div>

            <div className="widget-small card">
              <div className="label">‚≠ê Apostas populares</div>
              <div className="value">Top 5 vis√≠vel</div>
            </div>
          </div>

          {/* CENTER */}
          <div className="center-column">
            <div className="card">
              <h3>üéØ APOSTAS PRINCIPAIS</h3>
              <div style={{height:12}} />
              <div className="main-bets">
                {betsData.mainBets.map(m => (
                  <div key={m.id} className="main-bet-item" onClick={() => addToSlip(m, "Principal")}>
                    <div className="label"> {m.label} </div>
                    <div className="odds"> {m.odds.toFixed(2)} </div>
                  </div>
                ))}
              </div>
            </div>

            <div className="card">
              <h3>üìö OUTRAS APOSTAS</h3>
              <div style={{height:12}} />
              <OtherBetsBox categories={betsData.otherBets} onPick={(it, cat) => addToSlip(it, cat.category)} />
            </div>
          </div>

          {/* RIGHT */}
          <div className="right-column">
            <div className="betslip card">
              <h4>üßæ APOSTAS SELECIONADAS</h4>
              {slip.length === 0 && <div className="small-note">Nenhuma aposta selecionada</div>}
              {slip.map((s, idx) => (
                <div className="line" key={s.id}>
                  <div className="label">
                    {s.label}
                    <div style={{fontSize:12,color:'var(--muted)'}}>({s.oddLabel})</div>
                  </div>

                  <div className="controls">
                    <input className="input-amt" type="number" placeholder="Valor" value={s.amount||''} onChange={e => changeAmt(idx, e.target.value)} />
                    <button className="btn" onClick={() => place(idx)}>Apostar</button>
                    <button className="btn ghost" onClick={() => remove(idx)}>Remover</button>
                  </div>
                </div>
              ))}
              <div style={{marginTop:10, fontWeight:800, color:'var(--muted)'}}>Poss√≠vel retorno: <span style={{color:'var(--accent)'}}>{
