// src/pages/Dashboard.jsx
import React, { useState, useEffect } from "react";
import "../styles/Dashboard.css";
import betsData from "../data/betsData";
import CouplesSelector from "../components/CouplesSelector";
import OtherBetsBox from "../components/OtherBetsBox";
import { addHistory } from "../utils/apiMock";

// Views
import InicioView from "./views/InicioView";
import ApostasView from "./views/ApostasView";
import ResultadosView from "./views/ResultadosView";
import TendenciasView from "./views/TendenciasView";
import HistoricoView from "./views/HistoricoView";
import ContaView from "./views/ContaView";

export default function Dashboard({ user = {}, onNavigate, logout, toast }) {
  const [view, setView] = useState("inicio");
  const [selectedCouple, setSelectedCouple] = useState(betsData.couples[0].id);
  const [slip, setSlip] = useState([]);
  const [theme, setTheme] = useState(() => localStorage.getItem("theme") || "light");

  /* -------------------- TEMA -------------------- */
  useEffect(() => {
    document.body.classList.toggle("dark", theme === "dark");
    localStorage.setItem("theme", theme);
  }, [theme]);

  /* -------------------- ADD TO SLIP -------------------- */
  function addToSlip(item, labelPrefix = "Principal") {
    setSlip((p) => {
      if (p.find(x => x.id === item.id)) {
        toast("warn", "Essa aposta j√° est√° selecionada!");
        return p;
      }
      toast("info", `Aposta adicionada: ${item.label}`);
      return [...p, { id: item.id, label: item.label, odds: item.odds, oddLabel: labelPrefix, amount: 0 }];
    });
  }

  /* -------------------- ALTERAR VALOR -------------------- */
  function changeAmt(i, v) {
    setSlip((p) => {
      const c = [...p];
      c[i] = { ...c[i], amount: Number(v) || 0 };
      return c;
    });
  }

  /* -------------------- REMOVER -------------------- */
  function remove(i) {
    toast("info", "Aposta removida.");
    setSlip((p) => p.filter((_, idx) => idx !== i));
  }

  /* -------------------- APOSTAR (TOAST + HIST√ìRICO) -------------------- */
  function place(i) {
    const item = slip[i];

    if (!item || !item.amount || item.amount <= 0) {
      toast("error", "Valor inv√°lido!");
      return;
    }

    toast("success", `Aposta realizada em "${item.label}"! üéâ`);

    // Salva no hist√≥rico (Mock API)
    addHistory({
      date: new Date().toISOString(),
      label: item.label,
      value: item.amount,
      odds: item.odds
    });

    // remover do slip
    setSlip((p) => p.filter((_, idx) => idx !== i));
  }

  /* -------------------- LOGOUT -------------------- */
  function handleLogout() {
    toast("info", "Voc√™ saiu da conta.");
    logout(); // agora controla tudo via App.jsx
  }

  /* -------------------- TEND√äNCIAS -------------------- */
  const trends = betsData.mainBets.slice(0, 6).map((b, idx) => ({
    label: b.label,
    score: Math.max(50 - idx * 6, 12)
  }));

  /* -------------------- MENU LATERAL -------------------- */
  const Nav = () => (
    <div className="sidebar card" role="navigation">
      <div className="nav">

        <div className={`nav-item ${view === "inicio" ? "active" : ""}`}
            onClick={() => setView("inicio")}>
          üè† In√≠cio
        </div>

        <div className={`nav-item ${view === "apostas" ? "active" : ""}`}
            onClick={() => setView("apostas")}>
          üé≤ Apostas
        </div>

        <div className={`nav-item ${view === "resultados" ? "active" : ""}`}
            onClick={() => setView("resultados")}>
          üìà Resultados
        </div>

        <div className={`nav-item ${view === "tendencias" ? "active" : ""}`}
            onClick={() => setView("tendencias")}>
          üî• Tend√™ncias
        </div>

        <div className={`nav-item ${view === "historico" ? "active" : ""}`}
            onClick={() => setView("historico")}>
          üìú Hist√≥rico
        </div>

        <div className={`nav-item ${view === "conta" ? "active" : ""}`}
            onClick={() => setView("conta")}>
          üë§ Conta
        </div>

      </div>
    </div>
  );

  return (
    <div className="app-wrapper">
      <div className="dashboard-container">

        {/* TOPBAR */}
        <div className="topbar">

          <div className="left">
            <div className="brand" onClick={() => setView("inicio")}>üéØ BET DO T√âRMINO</div>
            <CouplesSelector couples={betsData.couples} value={selectedCouple} onChange={setSelectedCouple} />
          </div>

          <div className="actions">
            <div className="balance">üíé Saldo: <strong>{user.balance || 0} pts</strong></div>

            <button className="icon-btn" onClick={() => setTheme(t => t === "dark" ? "light" : "dark")}>
              {theme === "dark" ? "‚òÄÔ∏è Claro" : "üåô Escuro"}
            </button>

            <button className="icon-btn" onClick={handleLogout}>üö™ Sair</button>
          </div>

        </div>

        {/* GRID */}
        <div className="main-grid">

          <Nav />

          <div className="center-column">
            {view === "inicio" && <InicioView addToSlip={addToSlip} selectedCouple={selectedCouple} />}
            {view === "apostas" && <ApostasView categories={betsData.otherBets} addToSlip={addToSlip} />}
            {view === "resultados" && <ResultadosView />}
            {view === "tendencias" && <TendenciasView trends={trends} />}
            {view === "historico" && <HistoricoView />}
            {view === "conta" && <ContaView user={user} onLogout={handleLogout} />}
          </div>

          {/* BETSLIP */}
          <div className="right-column">

            <div className="betslip card">
              <h4>üßæ Apostas Selecionadas</h4>

              {slip.length === 0 && <div className="small-note">Nenhuma aposta selecionada</div>}

              {slip.map((s, idx) => (
                <div className="line" key={s.id}>
                  <div className="label">
                    {s.label}
                    <div style={{ fontSize: 12, opacity: 0.7 }}>({s.oddLabel})</div>
                  </div>

                  <div className="controls">
                    <input className="input-amt" type="number" placeholder="Valor"
                      value={s.amount || ''}
                      onChange={e => changeAmt(idx, e.target.value)}
                    />

                    <button className="btn" onClick={() => place(idx)}>Apostar</button>
                    <button className="btn ghost" onClick={() => remove(idx)}>Remover</button>
                  </div>
                </div>
              ))}

              <div style={{ marginTop: 10, fontWeight: 800 }}>
                Poss√≠vel retorno:
                <span style={{ color: 'var(--accent)' }}>
                  {" "}{slip.reduce((acc, it) => acc + (it.amount || 0) * it.odds, 0).toFixed(2)}
                </span>
              </div>

            </div>

            <div className="trends card">
              <h3>üìä Tend√™ncias</h3>
              <div style={{ height: 8 }} />

              {trends.map((t, i) => (
                <div className="trend-row" key={i}>
                  <div className="t-label">{t.label}</div>
                  <div className="t-score">{t.score}%</div>
                </div>
              ))}

              <div style={{ height: 8 }} />
              <div className="small-note">Dados simulados ‚Ä¢ Conectar backend para reais</div>
            </div>

          </div>

        </div>
      </div>
    </div>
  );
}
