// src/pages/Bets.jsx
import React, { useEffect, useState } from "react";
import api from "../api/api";
import showToast from "../utils/showToast";
import BetSlip from "../components/BetSlip";
import "./bets.css";


export default function Bets() {
  const [bets, setBets] = useState([]);
  const [slip, setSlip] = useState([]);

  // ============================
  // CARREGA APOSTAS DO BACKEND
  // ============================
  const loadBets = async () => {
    try {
      const res = await api.get("/api/bets");
      setBets(res.data.bets || []);
    } catch (err) {
      showToast("Erro ao carregar apostas", "error");
    }
  };

  useEffect(() => {
    loadBets();
  }, []);

  // ============================
  // ADICIONA AO SLIP
  // ============================
  const addToSlip = (bet, option) => {
    const exists = slip.find((s) => s.betId === bet._id && s.optionId === option.id);
    if (exists) return showToast("Essa seleção já está no bilhete.", "info");

    const slipItem = {
      betId: bet._id,
      betTitle: bet.title,
      optionId: option.id,
      label: option.label,
      odds: option.odds,
      amount: 0,
    };

    setSlip((prev) => [...prev, slipItem]);
    showToast("Adicionado ao bilhete!", "success");
  };

  return (
    <div className="bets-page-container">
      <div className="bets-left">
        <h1 className="bets-title">Possíveis Términos</h1>

        {bets.length === 0 && (
          <p className="empty">Nenhuma aposta disponível no momento.</p>
        )}

        {bets.map((bet) => (
          <div key={bet._id} className="bet-card">
            <h2 className="category-title">{bet.title}</h2>
            <p className="sub">{bet.description}</p>

            <div className="bet-options">
              {bet.options.map((opt) => (
                <div key={opt.id} className="option-item">
                  <div className="label">{opt.label}</div>

                  <button
                    className="odd-btn"
                    onClick={() => addToSlip(bet, opt)}
                  >
                    {opt.odds.toFixed(2)}
                  </button>
                </div>
              ))}
            </div>

            <div className="footer">
              <p>Fecha em: <span>{new Date(bet.endsAt).toLocaleString()}</span></p>
            </div>
          </div>
        ))}
      </div>

      {/* SLIP */}
      <aside className="betslip-area">
        <BetSlip slip={slip} setSlip={setSlip} />
      </aside>
    </div>
  );
}
