import React, { useEffect, useState } from "react";
import api from "../api/api";
import showToast from "../utils/showToast";
src/pages/bets.css


export default function Bets() {
  const [bets, setBets] = useState([]);

  const loadBets = async () => {
    try {
      const res = await api.get("/bets");
      setBets(res.data.bets);
    } catch (err) {
      showToast("Erro ao carregar apostas", "error");
    }
  };

  useEffect(() => {
    loadBets();
  }, []);

  return (
    <div className="bets-page fade-in">
      <h1 className="bets-title">Apostas DisponÃ­veis</h1>

      {bets.length === 0 && (
        <p className="empty">Nenhuma aposta disponÃ­vel no momento.</p>
      )}

      <div className="bets-list">
        {bets.map((bet) => (
          <div key={bet._id} className="bet-card">
            <div className="bet-header">
              <h2>{bet.title}</h2>
              <p className="subtitle">Motivo do tÃ©rmino ðŸ’”</p>
            </div>

            <div className="bet-options">
              {bet.options.map((opt) => (
                <div key={opt.id} className="option-item">
                  <div className="label">{opt.label}</div>

                  <button
                    className="odd-btn"
                    onClick={() => showToast("Abrir slip futuramente", "info")}
                  >
                    {opt.odds.toFixed(2)}
                  </button>
                </div>
              ))}
            </div>

            <div className="footer">
              <p>Fecha em: <span>{new Date(bet.endsAt).toLocaleString()}</span></p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
