// src/pages/Dashboard.jsx
import React, { useState } from "react";
import "../styles/Dashboard.css";
import betsData from "../data/betsData";
import CouplesSelector from "../components/CouplesSelector";
import OtherBetsBox from "../components/OtherBetsBox";

export default function Dashboard({ user = {}, onNavigate }) {
  const [selectedCouple, setSelectedCouple] = useState(betsData.couples[0].id);
  const [slip, setSlip] = useState([]);

  // ADICIONAR À BETSLIP
  function addToSlip(item, labelPrefix = "Principal") {
    setSlip((p) => {
      if (p.find(x => x.id === item.id)) return p;
      return [...p, { 
        id: item.id, 
        label: item.label, 
        odds: item.odds, 
        oddLabel: labelPrefix, 
        amount: 0 
      }];
    });
  }

  // ALTERAR VALOR
  function changeAmt(i, v) {
    setSlip(p => {
      const c = [...p];
      c[i] = { ...c[i], amount: Number(v) || 0 };
      return c;
    });
  }

  // REMOVER DA BETSLIP
  function remove(i) {
    setSlip(p => p.filter((_, idx) => idx !== i));
  }

  // FAZER APOSTA
  function place(i) {
    const item = slip[i];
    if (!item.amount || item.amount <= 0) return alert("Valor inválido");

    alert(`Apostado ${item.amount} pts em ${item.label} — retorno ${(item.amount * item.odds).toFixed(2)}`);

    setSlip(p => p.filter((_, idx) => idx !== i));
  }

  // TENDÊNCIAS FAKE
  const trends = betsData.mainBets.slice(0,6).map((b, idx) => ({
    label: b.label,
    score: Math.max(50 - idx * 6, 12)
  }));

  return (
    <div className="app-wrapper">
      <div className="dashboard-container">

        {/* =============== TOPBAR =============== */}
        <div className="topbar">
          <div className="left">
            <div className="brand" onClick={() => onNavigate("inicio")}>
              BET DO TÉRMINO
            </div>
            <CouplesSelector 
              couples={betsData.couples} 
              value={selectedCouple} 
              onChange={setSelectedCouple} 
            />
          </div>

          <div className="actions">
            <div className="balance">
              Saldo: <strong>{user.balance || 0} pts</strong>
            </div>
            <button className="btn ghost" onClick={() => onNavigate("conta")}>
              Conta
            </button>
          </div>
        </div>

        {/* =============== GRID =============== */}
        <div className="main-grid">

          {/* =============== SIDEBAR =============== */}
          <div className="sidebar card">
            <div className="nav">
              <div className="nav-item active">Início</div>
              <div className="nav-item">Apostas</div>
              <div className="nav-item">Resultados</div>
              <div className="nav-item">Tendências</div>
              <div className="nav-item">Histórico</div>
            </div>

            <div style={{height:12}} />

            <div className="widget-small card">
              <div className="label">Casais em alta</div>
              <div className="value">3 casais</div>
            </div>

            <div className="widget-small card">
              <div className="label">Apostas populares</div>
              <div className="value">Top 5 visível</div>
            </div>
          </div>

          {/* =============== CENTRO =============== */}
          <div className="center-column">

            {/* ---- Apostas principais ---- */}
            <div className="card">
              <h3>APOSTAS PRINCIPAIS</h3>
              <div style={{height:12}} />

              <div className="main-bets">
                {betsData.mainBets.map(m => (
                  <div 
                    key={m.id} 
                    className="main-bet-item" 
                    onClick={() => addToSlip(m, "Principal")}
                  >
                    <div className="label">{m.label}</div>
                    <div className="odds">{m.odds.toFixed(2)}</div>
                  </div>
                ))}
              </div>
            </div>

            {/* ---- Outras Apostas (com categorias) ---- */}
            <div className="card">
              <h3>OUTRAS APOSTAS</h3>
              <div style={{height:12}} />

              <OtherBetsBox
                categories={betsData.otherBets}
                onPick={(it, cat) => addToSlip(it, cat.category)}
              />
            </div>
          </div>

          {/* =============== LADO DIREITO =============== */}
          <div className="right-column">

            {/* ---- BETSLIP ---- */}
            <div className="betslip card">
              <h4>APOSTAS SELECIONADAS</h4>

              {slip.length === 0 && (
                <div className="small-note">Nenhuma aposta selecionada</div>
              )}

              {slip.map((s, idx) => (
                <div className="line" key={s.id}>
                  <div className="label">
                    {s.label}
                    <div style={{fontSize:12,color:'var(--muted)'}}>({s.oddLabel})</div>
                  </div>

                  <div className="controls">
                    <input
                      className="input-amt"
                      type="number"
                      placeholder="Valor"
                      value={s.amount || ""}
                      onChange={e => changeAmt(idx, e.target.value)}
                    />
                    <button className="btn" onClick={() => place(idx)}>Apostar</button>
                    <button className="btn ghost" onClick={() => remove(idx)}>Remover</button>
                  </div>
                </div>
              ))}

              <div style={{marginTop:10, fontWeight:800, color:'var(--muted)'}}>
                Possível retorno: 
                <span style={{color:'var(--accent)'}}>
                  {slip.reduce((a, it) => a + (it.amount||0)*it.odds, 0).toFixed(2)}
                </span>
              </div>
            </div>

            {/* ---- Tendências ---- */}
            <div className="trends card">
              <h3 style={{color:'#00262e'}}>Tendências de Término</h3>
              <div style={{height:8}} />

              {trends.map((t, i) => (
                <div className="trend-row" key={i}>
                  <div className="t-label">{t.label}</div>
                  <div className="t-score">{t.score}%</div>
                </div>
              ))}

              <div style={{height:8}} />
              <div className="small-note">Dados simulados</div>
            </div>

            {/* ---- Últimas apostas ---- */}
            <div className="widget-small card">
              <div className="label">Últimas apostas</div>
              <div className="value">Sem dados</div>
            </div>

          </div>
        </div>
      </div>
    </div>
  );
}
