import React, { useEffect, useState } from "react";
import api from "../api/api";
import "../styles/Dashboard.css";
import showToast from "../utils/showToast";
import BetSlipSidebar from "../components/BetSlipSidebar";

export default function Dashboard() {
  const [saldo, setSaldo] = useState(0);
  const [bets, setBets] = useState([]);
  const [slip, setSlip] = useState([]);

  // Carregar saldo
  useEffect(() => {
    setSaldo(125.40); // futuramente vir√° do backend
  }, []);

  // Carregar apostas do backend
  const loadBets = async () => {
    try {
      const res = await api.get("/bets");
      setBets(res.data.bets || []);
    } catch (err) {
      showToast("Erro ao carregar apostas!", "error");
    }
  };

  useEffect(() => {
    loadBets();
  }, []);

  // Adicionar aposta ao slip
  const addToSlip = (bet, opt) => {
    setSlip((prev) => [
      ...prev,
      {
        betId: bet._id,
        betTitle: bet.title,
        optionId: opt.id,
        label: opt.label,
        odds: opt.odds,
        amount: 0,
      },
    ]);
    showToast("Adicionado ao bilhete!", "success");
  };

  return (
    <div className="dashboard fade-in">

      {/* ===================== FOTO DO CASAL ===================== */}
      <div className="casal-card">
        <div className="casal-foto">
          <img src="/img/foto-1.jpg" alt="Pessoa 1" />
        </div>

        <div className="casal-foto">
          <img src="/img/foto-2.jpg" alt="Pessoa 2" />
        </div>
      </div>

      <h2 className="title-casal">Alef & Larissa ‚ù§Ô∏è</h2>

      {/* ===================== APOSTAS EM ALTA ===================== */}
      <h2 className="sec-title">üî• Apostas em Alta</h2>

      <div className="apostas-alta">
        {bets.slice(0, 4).map((bet) => (
          <div key={bet._id} className="alta-card card-anim">

            <h3 className="alta-title">{bet.title}</h3>

            <div className="alta-options">
              {bet.options.slice(0, 2).map((opt) => (
                <button
                  key={opt.id}
                  className="alta-btn"
                  onClick={() => addToSlip(bet, opt)}
                >
                  <span>{opt.label}</span>
                  <b>{opt.odds.toFixed(2)}</b>
                </button>
              ))}
            </div>
          </div>
        ))}
      </div>

      {/* =========================================================== */}
      {/* ============== SIDEBAR DO BETSLIP NO INICIAL ============= */}
      {/* =========================================================== */}
      <BetSlipSidebar slip={slip} setSlip={setSlip} />
    </div>
  );
}
