// src/pages/Dashboard.jsx
import React, { useState } from "react";
import "../styles/Dashboard.css";
import betsData from "../data/betsData";
import OtherBetsBox from "../components/OtherBetsBox";
import CouplesSelector from "../components/CouplesSelector";

export default function Dashboard({ user = {}, onNavigate, onLogout }) {
  const [selectedCouple, setSelectedCouple] = useState(betsData.couples[0].id);
  const [slip, setSlip] = useState([]);

  function addToSlip(item, labelPrefix = "Principal") {
    setSlip(prev => {
      if (prev.find(x => x.id === item.id)) return prev;
      return [...prev, { id: item.id, label: item.label, odds: item.odds, oddLabel: labelPrefix, amount: 0 }];
    });
  }

  function changeAmt(i, v) {
    setSlip(p => {
      const copy = [...p];
      copy[i] = { ...copy[i], amount: Number(v) || 0 };
      return copy;
    });
  }

  function remove(i) {
    setSlip(p => p.filter((_, idx) => idx !== i));
  }

  function place(i) {
    const it = slip[i];
    if (!it || !it.amount || it.amount <= 0) return alert("Valor inv√°lido");
    alert(`Apostado ${it.amount} pts em ${it.label} ‚Äî poss√≠vel retorno ${(it.amount * it.odds).toFixed(2)}`);
    setSlip(p => p.filter((_, idx) => idx !== i));
  }

  const totalReturn = slip.reduce((acc, it) => acc + (it.amount || 0) * (it.odds || 0), 0);

  return (
    <div className="app-wrapper">
      <div className="dashboard-container">
        <div className="topbar">
          <div style={{display:"flex",alignItems:"center",gap:12}}>
            <div className="brand" onClick={() => onNavigate("dashboard")}>üéØ BET DO T√âRMINO</div>
            <CouplesSelector couples={betsData.couples} value={selectedCouple} onChange={setSelectedCouple} />
          </div>

          <div className="actions">
            <div className="balance">üíé Saldo: <strong>{user.balance || 0} pts</strong></div>
            <button className="icon-btn" onClick={() => onNavigate("conta")}>Conta</button>
            <button className="icon-btn" onClick={onLogout}>üö™ Sair</button>
          </div>
        </div>

        <div className="main-grid">
          <div className="sidebar card">
            <div className="nav">
              <div className="nav-item active">üè† In√≠cio</div>
              <div className="nav-item" onClick={() => onNavigate("dashboard")}>üé≤ Apostas</div>
              <div className="nav-item" onClick={() => onNavigate("patrocinadores")}>ü§ù Patrocinadores</div>
              <div className="nav-item" onClick={() => onNavigate("historico")}>üìú Hist√≥rico</div>
            </div>

            <div className="widget-small card" style={{marginTop:16}}>
              <div className="label">Casais</div>
              <div className="value">{betsData.couples.length} listados</div>
            </div>
          </div>

          <div className="center-column">
            <div className="card">
              <h3>üéØ APOSTAS PRINCIPAIS</h3>
              <div className="main-bets">
                {betsData.mainBets.map(m => (
                  <div key={m.id} className="main-bet-item" onClick={() => addToSlip(m,"Principal")}>
                    <div>{m.label}</div>
                    <div className="odds">{m.odds.toFixed(2)}</div>
                  </div>
                ))}
              </div>
            </div>

            <div className="card">
              <h3>üìö OUTRAS APOSTAS</h3>
              <OtherBetsBox categories={betsData.otherBets} onPick={(it, cat) => addToSlip(it, cat.category)} />
            </div>
          </div>

          <div className="right-column">
            <div className="card betslip">
              <h4>üßæ APOSTAS SELECIONADAS</h4>
              {slip.length === 0 && <div className="small-note">Nenhuma aposta selecionada</div>}
              {slip.map((s, idx) => (
                <div key={s.id} className="line">
                  <div style={{flex:1}}>
                    <div style={{fontWeight:800}}>{s.label}</div>
                    <div style={{fontSize:12,color:"var(--text-soft)"}}>{s.oddLabel}</div>
                  </div>
                  <div style={{display:"flex",flexDirection:"column",gap:8,alignItems:"flex-end"}}>
                    <input className="input-amt" type="number" placeholder="Valor" value={s.amount||""} onChange={e=>changeAmt(idx,e.target.value)} />
                    <div style={{display:"flex",gap:8}}>
                      <button className="btn" onClick={()=>place(idx)}>Apostar</button>
                      <button className="btn ghost" onClick={()=>remove(idx)}>Remover</button>
                    </div>
                  </div>
                </div>
              ))}
              <div style={{marginTop:12,fontWeight:800}}>Poss√≠vel retorno: <span style={{color:"var(--accent)"}}>{totalReturn.toFixed(2)}</span></div>
            </div>

            <div className="card trends">
              <h3>üìä Tend√™ncias de T√©rmino</h3>
              <div style={{marginTop:8}}>
                {betsData.mainBets.slice(0,5).map((b,i)=>(
                  <div key={b.id} className="trend-row">
                    <div>{b.label}</div>
                    <div className="t-score">{Math.max(10, 60 - i*8)}%</div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>

      </div>
    </div>
  );
}
