// src/components/BetSlip.jsx

import React, { useState } from "react";
import api from "../api/api";
import showToast from "../utils/showToast";
import "../styles/betslip.css";

export default function BetSlip({ slip, setSlip }) {
  const [loadingIndex, setLoadingIndex] = useState(null);

  // REMOVE ITEM DO SLIP
  const removeItem = (index) => {
    setSlip((prev) => prev.filter((_, i) => i !== index));
  };

  // ATUALIZA VALOR DA APOSTA
  const updateAmount = (index, value) => {
    setSlip((prev) => {
      const copy = [...prev];
      copy[index].amount = Number(value);
      return copy;
    });
  };

  // ENVIA A APOSTA PARA O BACKEND
  const sendBet = async (item, index) => {
    if (!item.amount || item.amount <= 0) {
      showToast("Digite um valor válido!", "warning");
      return;
    }

    setLoadingIndex(index);

    try {
      const res = await api.post(`/bets/${item.betId}/wager`, {
        optionId: item.optionId,
        amount: Number(item.amount),
      });

      showToast("Aposta registrada!", "success");

      // Atualiza saldo do usuário
      if (res.data.newBalance !== undefined) {
        localStorage.setItem("balance", res.data.newBalance);
      }

      // Remove a aposta do slip
      removeItem(index);
    } catch (err) {
      showToast(
        err?.response?.data?.message || "Erro ao registrar aposta.",
        "error"
      );
    }

    setLoadingIndex(null);
  };

  // CALCULA RETORNO TOTAL
  const totalReturn = slip.reduce((sum, s) => {
    if (!s.amount) return sum;
    return sum + s.amount * (s.odds || 1);
  }, 0);

  return (
    <div className="betslip-box">
      <h2 className="betslip-title">Bilhete</h2>

      {slip.length === 0 && (
        <p className="empty-slip">Nenhuma seleção adicionada.</p>
      )}

      {slip.map((item, index) => (
        <div key={index} className="betslip-item">
          <div className="item-info">
            <p className="bet-title">{item.betTitle}</p>
            <p className="option">{item.label}</p>
            <p className="odds">Odd: {item.odds?.toFixed(2) ?? "1.00"}</p>
          </div>

          <input
            type="number"
            className="amount-input"
            placeholder="Valor"
            min="1"
            value={item.amount || ""}
            onChange={(e) => updateAmount(index, e.target.value)}
          />

          <div className="btn-row">
            <button
              className="confirm-btn"
              onClick={() => sendBet(item, index)}
              disabled={loadingIndex === index}
            >
              {loadingIndex === index ? "Apostando..." : "Apostar"}
            </button>

            <button
              className="remove-btn"
              onClick={() => removeItem(index)}
            >
              Remover
            </button>
          </div>
        </div>
      ))}

      {slip.length > 0 && (
        <p className="total-return">
          Possível retorno: <b>R$ {totalReturn.toFixed(2)}</b>
        </p>
      )}
    </div>
  );
}
