// src/components/OtherBetsBox.jsx
import React, { useState } from "react";
import "../styles/Dashboard.css";

export default function OtherBetsBox({ categories = [], onPick }) {
  const [openCategory, setOpenCategory] = useState(null);

  const toggle = (c) => setOpenCategory(prev => prev === c ? null : c);

  const emojiFor = name => {
    if (/quem termina/i.test(name)) return "‚û°Ô∏è";
    if (/dura√ß√£o/i.test(name)) return "‚è≥";
    if (/motivo/i.test(name)) return "üíî";
    if (/forma/i.test(name)) return "üì±";
    return "üîπ";
  };

  return (
    <div className="other-bets-container">
      {categories.map(cat => (
        <div className="other-category-block" key={cat.category}>
          <div className="other-category-header" onClick={() => toggle(cat.category)}>
            <div style={{display:"flex",gap:10,alignItems:"center"}}><span>{emojiFor(cat.category)}</span><strong>{cat.category}</strong></div>
            <div>{openCategory === cat.category ? "‚ñ≤" : "‚ñº"}</div>
          </div>

          <div className={`other-bets-list ${openCategory === cat.category ? "open" : ""}`}>
            {cat.items.map(it => (
              <div className="other-bet-item" key={it.id} onClick={() => onPick(it, cat)}>
                <div>{it.label}</div>
                <div className="odds">{it.odds.toFixed(2)}</div>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}
