import React, { useState } from "react";
import api from "../api/api";
import showToast from "../utils/showToast";
import "./betslip.css"; // criaremos depois

export default function BetSlip({ bet, option, close, refresh }) {
  const [amount, setAmount] = useState("");
  const [loading, setLoading] = useState(false);

  const submitWager = async () => {
    if (!amount || amount <= 0) {
      showToast("Digite um valor válido!", "warning");
      return;
    }

    setLoading(true);

    try {
      const res = await api.post(`/bets/${bet._id}/wager`, {
        optionId: option.id,
        amount: Number(amount)
      });

      if (res.data.newBalance !== undefined) {
        localStorage.setItem("balance", res.data.newBalance);
      }

      showToast("Aposta registrada!", "success");

      setAmount("");
      close();
      refresh();
    } catch (err) {
      showToast("Erro ao registrar aposta.", "error");
    }

    setLoading(false);
  };

  const returnValue = amount ? (amount * option.odds).toFixed(2) : "0.00";

  return (
    <div className="betslip-overlay">
      <div className="betslip-box">

        <h3>Confirmar Aposta</h3>

        <p><b>Aposta:</b> {bet.title}</p>
        <p><b>Opção:</b> {option.label}</p>
        <p><b>Odd:</b> {option.odds.toFixed(2)}</p>

        <input
          type="number"
          placeholder="Valor da aposta"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
        />

        <p className="return">
          Retorno previsto: <b>R$ {returnValue}</b>
        </p>

        <div className="btns">
          <button className="cancel" onClick={close}>Cancelar</button>
          <button className="confirm" onClick={submitWager} disabled={loading}>
            {loading ? "Enviando..." : "Confirmar"}
          </button>
        </div>

      </div>
    </div>
  );
}
