import React from "react";
import "../styles/betslip.css";

export default function BetSlipSidebar({ slip, setSlip, slipAberto, fechar }) {
  const updateAmount = (index, value) => {
    const newSlip = [...slip];
    newSlip[index].amount = value;
    setSlip(newSlip);
  };

  const removeItem = (index) => {
    const newSlip = slip.filter((_, i) => i !== index);
    setSlip(newSlip);
  };

  return (
    <div className={`betslip-sidebar ${slipAberto ? "ativo" : ""}`}>
      
      <div className="betslip-header">
        <h2>ðŸŽ« Seu Bilhete</h2>
        <button className="close-btn" onClick={fechar}>âœ–</button>
      </div>

      {slip.length === 0 ? (
        <p className="empty-slip">Nenhuma aposta selecionada.</p>
      ) : (
        <div className="slip-items">
          {slip.map((item, index) => (
            <div key={index} className="slip-item">
              
              <div className="slip-title">{item.betTitle}</div>
              <div className="slip-option">{item.label}</div>

              <div className="slip-odds">Odd: {item.odds.toFixed(2)}</div>

              <input
                type="number"
                placeholder="Valor"
                value={item.amount}
                onChange={(e) => updateAmount(index, Number(e.target.value))}
              />

              <button className="remove-btn" onClick={() => removeItem(index)}>
                Remover
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
