// src/pages/Bets.jsx

import React, { useEffect, useState } from "react";
import api from "../api/api";
import { useNavigate } from "react-router-dom";
import "../styles/Bets.css";

export default function Bets() {
  const navigate = useNavigate();
  const [bets, setBets] = useState([]);
  const [loading, setLoading] = useState(true);

  const loadBets = async () => {
    try {
      const res = await api.get("/api/bets");
      setBets(res.data.bets || []);
    } catch (err) {
      console.error(err);
    }
    setLoading(false);
  };

  useEffect(() => {
    loadBets();
  }, []);

  if (loading) {
    return <p className="loading-text">Carregando apostas...</p>;
  }

  return (
    <div className="bets-container">
      <h2>Apostas Dispon√≠veis</h2>

      {bets.length === 0 && (
        <p className="empty-text">Nenhuma aposta aberta no momento.</p>
      )}

      {bets.map((bet) => (
        <div className="bet-card" key={bet._id}>
          <h3>{bet.title}</h3>
          <p className="bet-desc">{bet.description}</p>

          <button
            className="bet-open-btn"
            onClick={() => navigate(`/apostas/${bet._id}`)}
          >
            Ver Detalhes
          </button>
        </div>
      ))}
    </div>
  );
}
