import React, { useState } from "react";
import "../styles/Dashboard.css";

export default function Conta({ user, onNavigate }) {
  const [amount, setAmount] = useState("");
  const [history, setHistory] = useState([]);

  function handleDeposit() {
    if (!amount || Number(amount) <= 0) return alert("Valor inválido");
    // aqui você chamaria API para depositar (simulamos)
    setHistory(prev => [{ type: "deposit", amount: Number(amount), date: new Date().toLocaleString() }, ...prev]);
    alert(`Depósito de ${amount} pts realizado (simulado)`);
    setAmount("");
  }

  function handleWithdraw() {
    if (!amount || Number(amount) <= 0) return alert("Valor inválido");
    // simulação
    setHistory(prev => [{ type: "withdraw", amount: Number(amount), date: new Date().toLocaleString() }, ...prev]);
    alert(`Saque de ${amount} pts solicitado (simulado)`);
    setAmount("");
  }

  return (
    <div className="account-page">
      <div className="account-header">
        <button className="btn ghost" onClick={() => onNavigate("dashboard")}>Voltar</button>
        <h2>CONTA</h2>
        <div />
      </div>

      <div className="account-body">
        <div className="account-left">
          <h3>Olá, {user?.username || "Usuário"}</h3>
          <div style={{ marginTop:10, fontSize:20, fontWeight:800 }}>{user?.balance || 0} pts</div>

          <div className="money-row">
            <input className="input-amt" placeholder="Valor" value={amount} onChange={e => setAmount(e.target.value)} />
            <button className="btn" onClick={handleDeposit}>Depositar</button>
            <button className="btn ghost" onClick={handleWithdraw}>Sacar</button>
          </div>

          <div style={{ marginTop:12 }}>
            <button className="btn ghost" onClick={() => setHistory([])}>Limpar Histórico (sim)</button>
          </div>
        </div>

        <div className="account-right">
          <h4>Histórico</h4>
          {history.length === 0 && <div className="small-note">Sem movimentos</div>}
          {history.map((h, idx) => (
            <div key={idx} style={{ padding:8, borderBottom:"1px solid #111" }}>
              <div style={{ fontWeight:700 }}>{h.type === "deposit" ? "Depósito" : "Saque"}</div>
              <div style={{ color:"#aaa", fontSize:13 }}>{h.date}</div>
              <div style={{ marginTop:6 }}>{h.amount} pts</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
