// src/pages/Dashboard.jsx
import React, { useState, useEffect } from "react";
import "../styles/Dashboard.css";
import betsData from "../data/betsData";
import CouplesSelector from "../components/CouplesSelector";

// Views
import InicioView from "./views/InicioView";
import ApostasView from "./views/ApostasView";
import ResultadosView from "./views/ResultadosView";
import HistoricoView from "./views/HistoricoView";

// NOVO: Conta Premium Modal
import ContaViewPremium from "./views/ContaViewPremium";

// ========== FUN√á√ÉO PARA FORMATAR EM R$ ==========
function formatBRL(n) {
  return Number(n).toLocaleString("pt-BR", {
    style: "currency",
    currency: "BRL",
  });
}

export default function Dashboard({ user = {}, onNavigate }) {
  const [view, setView] = useState("inicio");
  const [selectedCouple, setSelectedCouple] = useState(
    betsData.couples[0].id
  );
  const [slip, setSlip] = useState([]);
  const [theme, setTheme] = useState(
    () => localStorage.getItem("theme") || "light"
  );

  // NOVO: modal de conta
  const [showContaModal, setShowContaModal] = useState(false);

  // ========== THEME ==========
  useEffect(() => {
    document.body.classList.toggle("dark", theme === "dark");
    localStorage.setItem("theme", theme);
  }, [theme]);

  // ========== APOSTAS ==========
  function addToSlip(item, prefix = "Principal") {
    setSlip((p) => {
      if (p.find((x) => x.id === item.id)) return p;
      return [
        ...p,
        {
          id: item.id,
          label: item.label,
          odds: item.odds,
          oddLabel: prefix,
          amount: 0,
        },
      ];
    });
  }

  function changeAmt(index, val) {
    setSlip((p) => {
      const copy = [...p];
      copy[index].amount = Number(val) || 0;
      return copy;
    });
  }

  function removeBet(index) {
    setSlip((p) => p.filter((_, i) => i !== index));
  }

  function placeBet(index) {
    const item = slip[index];
    if (!item || item.amount <= 0) return alert("Valor inv√°lido.");

    alert(
      `Aposta realizada!\n\nValor: ${formatBRL(
        item.amount
      )}\nRetorno poss√≠vel: ${formatBRL(item.amount * item.odds)}`
    );

    setSlip((p) => p.filter((_, i) => i !== index));
  }

  // ========== LOGOUT ==========
  function handleLogout() {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    onNavigate("login");
  }

  return (
    <div className="app-wrapper">
      <div className="dashboard-container">
        {/* ===================== TOPBAR ===================== */}
        <div className="topbar">
          <div className="left">
            <div className="brand" onClick={() => setView("inicio")}>
              üéØ BET DO T√âRMINO
            </div>

            <CouplesSelector
              couples={betsData.couples}
              value={selectedCouple}
              onChange={setSelectedCouple}
            />
          </div>

          {/* ========== BOT√ïES DIREITA ========== */}
          <div className="actions">
            <div className="balance">
              üí∞ Saldo: <strong>{formatBRL(user.balance || 0)}</strong>
            </div>

            <button
              className="icon-btn"
              onClick={() => setTheme((t) => (t === "dark" ? "light" : "dark"))}
            >
              {theme === "dark" ? "‚òÄÔ∏è Light" : "üåô Dark"}
            </button>

            {/* NOVO ‚Üí bot√£o de conta aqui no topo */}
            <button
              className="icon-btn"
              onClick={() => setShowContaModal(true)}
            >
              üë§ Conta
            </button>

            <button className="icon-btn" onClick={handleLogout}>
              üö™ Sair
            </button>
          </div>
        </div>

        {/* ===================== GRID ===================== */}
        <div className="main-grid">
          {/* SIDEBAR */}
          <div className="sidebar card">
            <div className="nav">
              <div
                className={`nav-item ${view === "inicio" ? "active" : ""}`}
                onClick={() => setView("inicio")}
              >
                üè† In√≠cio
              </div>

              <div
                className={`nav-item ${view === "apostas" ? "active" : ""}`}
                onClick={() => setView("apostas")}
              >
                üé≤ Apostas
              </div>

              <div
                className={`nav-item ${view === "resultados" ? "active" : ""}`}
                onClick={() => setView("resultados")}
              >
                üìà Resultados
              </div>

              <div
                className={`nav-item ${
                  view === "historico" ? "active" : ""
                }`}
                onClick={() => setView("historico")}
              >
                üìú Hist√≥rico
              </div>
            </div>
          </div>

          {/* CENTRO */}
          <div className="center-column">
            {view === "inicio" && (
              <InicioView
                addToSlip={addToSlip}
                selectedCouple={selectedCouple}
              />
            )}
            {view === "apostas" && (
              <ApostasView
                categories={betsData.otherBets}
                addToSlip={addToSlip}
              />
            )}
            {view === "resultados" && <ResultadosView />}
            {view === "historico" && <HistoricoView />}
          </div>

          {/* DIREITA - BETSLIP */}
          <div className="right-column">
            <div className="betslip card">
              <h4>üßæ Apostas Selecionadas</h4>

              {slip.length === 0 && (
                <div className="small-note">Nenhuma aposta selecionada</div>
              )}

              {slip.map((s, idx) => (
                <div className="line" key={s.id}>
                  <div className="label">
                    {s.label}
                    <div className="odd">{s.oddLabel}</div>
                  </div>

                  <div className="controls">
                    <input
                      className="input-amt"
                      type="number"
                      placeholder="Valor"
                      value={s.amount || ""}
                      onChange={(e) => changeAmt(idx, e.target.value)}
                    />

                    <button
                      className="btn"
                      onClick={() => placeBet(idx)}
                    >
                      Apostar
                    </button>

                    <button
                      className="btn ghost"
                      onClick={() => removeBet(idx)}
                    >
                      Remover
                    </button>
                  </div>
                </div>
              ))}

              <div className="return">
                Retorno poss√≠vel:{" "}
                <strong>
                  {formatBRL(
                    slip.reduce(
                      (acc, it) => acc + (it.amount || 0) * it.odds,
                      0
                    )
                  )}
                </strong>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* ===================== MODAL PREMIUM ===================== */}
      {showContaModal && (
        <ContaViewPremium
          user={user}
          formatBRL={formatBRL}
          close={() => setShowContaModal(false)}
        />
      )}
    </div>
  );
}
