// src/components/BetSlipSidebar.jsx
import React, { useState } from "react";
import api from "../api/api";
import showToast from "../utils/showToast";
import "../styles/betslip-sidebar.css";

export default function BetSlipSidebar({ slip, setSlip }) {
  const [open, setOpen] = useState(false);
  const [loadingIndex, setLoadingIndex] = useState(null);

  const toggle = () => setOpen(!open);

  const removeItem = (index) => {
    const copy = [...slip];
    copy.splice(index, 1);
    setSlip(copy);
  };

  const updateAmount = (index, value) => {
    const copy = [...slip];
    copy[index].amount = Number(value);
    setSlip(copy);
  };

  const sendBet = async (item, index) => {
    if (!item.amount || item.amount <= 0) {
      showToast("Digite um valor v√°lido!", "warning");
      return;
    }

    setLoadingIndex(index);

    try {
      const res = await api.post(`/bets/${item.betId}/wager`, {
        optionId: item.optionId,
        amount: item.amount,
      });

      showToast("Aposta realizada com sucesso!", "success");

      if (res.data.newBalance !== undefined)
        localStorage.setItem("balance", res.data.newBalance);

      removeItem(index);
    } catch (err) {
      showToast(err.response?.data?.message || "Erro ao apostar", "error");
    }

    setLoadingIndex(null);
  };

  const totalReturn = slip.reduce((acc, item) => {
    if (!item.amount) return acc;
    return acc + item.amount * item.odds;
  }, 0);

  return (
    <>
      {/* BOT√ÉO FLOTANTE */}
      <div className="slip-button" onClick={toggle}>
        üßæ
      </div>

      {/* SIDEBAR */}
      <div className={`slip-sidebar ${open ? "open" : ""}`}>
        <h2>Seu Bilhete</h2>

        {slip.length === 0 && <p className="empty">Nenhuma aposta adicionada.</p>}

        {slip.map((item, index) => (
          <div key={index} className="slip-item">
            <div className="info">
              <b>{item.betTitle}</b>
              <p>{item.label}</p>
              <span className="odds">Odd: {item.odds.toFixed(2)}</span>
            </div>

            <input
              type="number"
              placeholder="Valor"
              value={item.amount || ""}
              onChange={(e) => updateAmount(index, e.target.value)}
            />

            <button
              className="confirm-btn"
              disabled={loadingIndex === index}
              onClick={() => sendBet(item, index)}
            >
              {loadingIndex === index ? "Apostando..." : "Apostar"}
            </button>

            <button className="remove-btn" onClick={() => removeItem(index)}>
              Remover
            </button>
          </div>
        ))}

        {slip.length > 0 && (
          <p className="total-return">
            Retorno poss√≠vel: <b>{totalReturn.toFixed(2)}</b>
          </p>
        )}
      </div>
    </>
  );
}
