  ];

  return (
    <div>
      <div className="card">
        <h3>ðŸ“œ HistÃ³rico de Apostas</h3>
        <p className="small-note">Todas as apostas feitas por vocÃª.</p>
      </div>

      <div style={{height:12}} />

      <div className="card">
        {history.map(h => (
          <div key={h.id} style={{display:"flex", justifyContent:"space-between", padding:"10px 0", borderBottom:"1px solid var(--border)"}}>
