// src/pages/Conta.jsx
import React, { useState } from "react";
import "../styles/Dashboard.css";

export default function Conta({ user = {}, onNavigate }) {
  const [amount, setAmount] = useState("");
  const [history, setHistory] = useState([]);

  function deposit() {
    if (!amount || Number(amount) <= 0) return alert("Valor inv谩lido");
    setHistory(h => [{ type: "deposit", amount: Number(amount), date: new Date().toLocaleString() }, ...h]);
    alert(`Dep贸sito de ${amount} pts realizado (simulado)`);
    setAmount("");
  }
  function withdraw() {
    if (!amount || Number(amount) <= 0) return alert("Valor inv谩lido");
    setHistory(h => [{ type: "withdraw", amount: Number(amount), date: new Date().toLocaleString() }, ...h]);
    alert(`Saque de ${amount} pts solicitado (simulado)`);
    setAmount("");
  }

  return (
    <div className="app-wrapper">
      <div className="dashboard-container">
        <div className="topbar">
          <div className="brand" onClick={() => onNavigate("dashboard")}> BET DO TRMINO</div>
          <div>
            <button className="icon-btn" onClick={() => onNavigate("dashboard")}>Voltar</button>
          </div>
        </div>

        <div style={{marginTop:18, display:"grid", gridTemplateColumns:"1fr 360px", gap:20}}>
          <div className="card">
            <h3>Conta</h3>
            <div style={{marginTop:10}}>
              <h2>Ol谩, {user.username}</h2>
              <div style={{fontSize:20, fontWeight:800, marginTop:8}}>{user.balance || 0} pts</div>

              <div style={{marginTop:12, display:"flex", gap:8}}>
                <input className="input-amt" placeholder="Valor" value={amount} onChange={e=>setAmount(e.target.value)} />
                <button className="btn" onClick={deposit}>Depositar</button>
                <button className="btn ghost" onClick={withdraw}>Sacar</button>
              </div>
            </div>
          </div>

          <div className="card">
            <h3>Hist贸rico</h3>
            {history.length === 0 && <div className="small-note">Sem movimentos</div>}
            {history.map((h, i) => (
              <div key={i} style={{padding:10, borderBottom:"1px solid var(--border)"}}>
                <div style={{fontWeight:700}}>{h.type === "deposit" ? "Dep贸sito" : "Saque"}</div>
                <div style={{fontSize:13,color:"var(--text-soft)"}}>{h.date}</div>
                <div style={{marginTop:6}}>{h.amount} pts</div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}
