import React from "react";
import { Link } from "react-router-dom";
import { FaHeartBroken, FaUser, FaSignOutAlt, FaTicketAlt } from "react-icons/fa";
import "../pages/dashboard.css";

export default function Dashboard() {
  return (
    <div className="dashboard-container">

      {/* ÍCONE DO BETSLIP NO CANTO DIREITO */}
      <Link to="/betslip" className="betslip-icon">
        <FaTicketAlt size={32} />
      </Link>

      <h1 className="dashboard-title">CAUÃBET</h1>

      <div className="menu-grid">

        <Link to="/apostar" className="menu-item">
          <FaHeartBroken size={28} />
          <span>Apostar</span>
        </Link>

        <Link to="/minhas-apostas" className="menu-item">
          <FaTicketAlt size={28} />
          <span>Minhas Apostas</span>
        </Link>

        <Link to="/perfil" className="menu-item">
          <FaUser size={28} />
          <span>Perfil</span>
        </Link>

        <Link to="/logout" className="menu-item logout">
          <FaSignOutAlt size={28} />
          <span>Sair</span>
        </Link>

      </div>
    </div>
  );
}
