import React from "react";
import api from "../api/api";               // <-- caminho corrigido
import showToast from "../utils/showToast"; // <-- default import correto

export default function BetSlip({ slip, onChangeAmount, onRemove, refresh }) {

  // ===========================
  // FUNÇÃO DE FAZER APOSTA
  // ===========================
  const handlePlace = async (idx) => {
    const item = slip[idx];
    if (!item) return;

    if (!item.amount || item.amount <= 0) {
      showToast("Insira um valor válido!", "error");
      return;
    }

    try {
      const token = localStorage.getItem("token");

      await api.post(
        `/bets/${item.betId}/wager`,
        {
          optionId: item.id,
          amount: Number(item.amount),
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      showToast("Aposta registrada com sucesso!", "success");

      onRemove(idx);

      if (refresh) refresh();

    } catch (err) {
      const status = err.response?.status;
      const msg = err.response?.data?.message;

      if (status === 402) {
        showToast("Saldo insuficiente!", "error");
      } else {
        showToast(msg || "Erro ao fazer aposta.", "error");
      }
    }
  };

  // ===========================
  // CÁLCULO DO RETORNO
  // ===========================
  const total = slip.reduce(
    (s, item) => s + (item.amount || 0) * (item.odds || 0),
    0
  );

  // ===========================
  // RENDERIZAÇÃO
  // ===========================
  return (
    <div className="betslip">
      <h4>APOSTA SELECIONADA</h4>

      {slip.length === 0 && <div className="small">Nenhuma seleção</div>}

      {slip.map((s, idx) => (
        <div key={s.id} className="line" style={{ alignItems: "center" }}>
          
          {/* Nome + odd */}
          <div>
            <div style={{ fontWeight: 700 }}>{s.label}</div>
            <div style={{ fontSize: 12, color: "#aaa" }}>
              {s.oddLabel} — {s.odds.toFixed(2)}
            </div>
          </div>

          {/* Valor + botões */}
          <div style={{ width: 110 }}>
            <input
              type="number"
              placeholder="0"
              value={s.amount || ""}
              onChange={(e) => onChangeAmount(idx, Number(e.target.value))}
            />

            <div style={{ marginTop: 6, display: "flex", gap: 6 }}>
              <button className="btn" onClick={() => handlePlace(idx)}>
                Apostar
              </button>

              <button className="btn ghost" onClick={() => onRemove(idx)}>
                Remover
              </button>
            </div>
          </div>

        </div>
      ))}

      {/* Retorno total */}
      <div style={{ marginTop: 10, fontWeight: 700 }}>
        Possível retorno: {total.toFixed(2)}
      </div>
    </div>
  );
}
