// src/pages/views/ContaViewPremium.jsx
import React, { useState } from "react";
import "./ContaViewPremium.css"; // vocÃª pode criar o CSS depois

export default function ContaViewPremium({ user, formatBRL, close }) {
  const [tab, setTab] = useState("saldo");
  const [valor, setValor] = useState("");
  const [history, setHistory] = useState(() => {
    return JSON.parse(localStorage.getItem("finHistory") || "[]");
  });

  function registrar(tipo) {
    if (!valor || valor <= 0) return alert("Valor invÃ¡lido.");

    const registro = {
      tipo,
      valor: Number(valor),
      data: new Date().toLocaleString(),
    };

    const novo = [registro, ...history];
    setHistory(novo);
    localStorage.setItem("finHistory", JSON.stringify(novo));
    setValor("");

    alert(`OperaÃ§Ã£o realizada: ${tipo.toUpperCase()} de ${formatBRL(registro.valor)}`);
  }

  return (
    <div className="conta-modal-overlay">
      <div className="conta-modal">
        <div className="conta-header">
          <h2>ðŸ‘¤ Minha Conta</h2>
          <button className="close-btn" onClick={close}>âœ–</button>
        </div>

        {/* MENU */}
        <div className="conta-tabs">
          <div className={`tab ${tab==="saldo"?"active":""}`} onClick={()=>setTab("saldo")}>ðŸ’° Saldo</div>
          <div className={`tab ${tab==="deposito"?"active":""}`} onClick={()=>setTab("deposito")}>âž• DepÃ³sito</div>
          <div className={`tab ${tab==="saque"?"active":""}`} onClick={()=>setTab("saque")}>âž– Saque</div>
          <div className={`tab ${tab==="historico"?"active":""}`} onClick={()=>setTab("historico")}>ðŸ“œ HistÃ³rico</div>
        </div>

        {/* CONTEÃšDO */}
        <div className="conta-content">

          {/* SALDO */}
          {tab === "saldo" && (
            <div>
              <h3>Saldo Atual</h3>
              <div className="saldo-box">{formatBRL(user.balance)}</div>
              <p className="mini-note">Saldo real em sua conta.</p>
            </div>
          )}

          {/* DEPOSITO */}
          {tab === "deposito" && (
            <div>
              <h3>Adicionar DepÃ³sito</h3>
              <input
                type="number"
                placeholder="Valor em R$"
                value={valor}
                onChange={(e) => setValor(e.target.value)}
              />
              <button className="main-btn" onClick={() => registrar("depÃ³sito")}>
                Depositar
              </button>
            </div>
          )}

          {/* SAQUE */}
          {tab === "saque" && (
            <div>
              <h3>Solicitar Saque</h3>
              <input
                type="number"
                placeholder="Valor em R$"
                value={valor}
                onChange={(e) => setValor(e.target.value)}
              />
              <button className="main-btn" onClick={() => registrar("saque")}>
                Solicitar Saque
              </button>
            </div>
          )}

          {/* HISTÃ“RICO */}
          {tab === "historico" && (
            <div>
              <h3>HistÃ³rico Financeiro</h3>

              {history.length === 0 && (
                <p className="mini-note">Nenhuma operaÃ§Ã£o registrada.</p>
              )}

              {history.map((h, i) => (
                <div key={i} className="hist-item">
                  <div>
                    <strong>{h.tipo.toUpperCase()}</strong> â€” {formatBRL(h.valor)}
                  </div>
                  <div className="hist-date">{h.data}</div>
                </div>
              ))}
            </div>
          )}

        </div>
      </div>
    </div>
  );
}
