// ========================== 
// src/App.jsx
// ==========================
import React, { useState, useEffect } from "react";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Dashboard from "./pages/Dashboard";
import Conta from "./pages/Conta";

export default function App() {
  const [page, setPage] = useState("login");
  const [user, setUser] = useState(null);

  // Mantém logado ao atualizar a página
  useEffect(() => {
    const savedUser = localStorage.getItem("user");
    const token = localStorage.getItem("token");

    if (savedUser && token) {
      setUser(JSON.parse(savedUser));
      setPage("dashboard");
    }
  }, []);

  const handleLogin = (userData) => {
    setUser(userData);
    setPage("dashboard");
  };

  return (
    <>
      {page === "login" && <Login onNavigate={setPage} onLogin={handleLogin} />}
      {page === "register" && <Register onNavigate={setPage} />}
      
      {/* DASHBOARD */}
      {page === "dashboard" && user && (
        <Dashboard user={user} onNavigate={setPage} />
      )}

      {/* CONTA */}
      {page === "conta" && user && (
        <Conta user={user} onNavigate={setPage} />
      )}
    </>
  );
}
