// src/pages/BetDetails.jsx

import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import api from "../api/api";
import BetSlip from "../components/BetSlip";
import ".//Bets.css";

export default function BetDetails() {
  const { id } = useParams();
  const [bet, setBet] = useState(null);
  const [wagers, setWagers] = useState([]);
  const [selectedOption, setSelectedOption] = useState(null);

  const loadBet = async () => {
    try {
      const res = await api.get(`/api/bets/${id}`);
      setBet(res.data.bet);
      setWagers(res.data.wagers);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    loadBet();
  }, [id]);

  if (!bet) return <p className="loading-text">Carregando...</p>;

  return (
    <div className="bets-container">
      <h2>{bet.title}</h2>
      <p className="bet-desc">{bet.description}</p>

      <h3>Opções</h3>
      <div className="options-list">
        {bet.options.map((option) => (
          <button
            key={option.id}
            className={
              selectedOption === option.id
                ? "option-btn option-selected"
                : "option-btn"
            }
            onClick={() => setSelectedOption(option.id)}
          >
            {option.label} — Odds: {option.odds}
          </button>
        ))}
      </div>

      {selectedOption && (
        <BetSlip bet={bet} optionId={selectedOption} refresh={loadBet} />
      )}

      <h3>Apostas Registradas</h3>
      {wagers.length === 0 && <p>Ninguém apostou ainda.</p>}

      {wagers.map((w) => (
        <div key={w._id} className="wager-card">
          <p>
            <b>{w.userId.username}</b> apostou <b>R$ {w.amount}</b> na opção{" "}
            <b>{w.optionId}</b>
          </p>
        </div>
      ))}
    </div>
  );
}
