// frontend/src/pages/Bets.jsx
import React, { useEffect, useState } from "react";
import api from "../api/api";
import showToast from "../utils/showToast";
import BetSlip from "../components/BetSlip";
import "./bets.css";

export default function Bets() {
  const [bets, setBets] = useState([]);
  const [loading, setLoading] = useState(true);

  // slip: array de { betId, optionId, label, odds, amount, betTitle }
  const [slip, setSlip] = useState([]);

  const loadBets = async () => {
    setLoading(true);
    try {
      const res = await api.get("/bets"); // usa baseURL = /api
      const list = res.data.bets || [];
      setBets(list);
    } catch (err) {
      console.error(err);
      showToast("Erro ao carregar apostas", "error");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadBets();
  }, []);

  // adicionar seleção ao slip
  const addToSlip = (bet, option) => {
    // Evita duplicar a mesma opção do mesmo bet
    const exists = slip.find(s => s.betId === bet._id && s.optionId === option.id);
    if (exists) {
      showToast("Seleção já está no bilhete", "info");
      return;
    }

    const item = {
      betId: bet._id,
      optionId: option.id,
      label: option.label,
      odds: option.odds,
      amount: 0,
      betTitle: bet.title,
    };

    setSlip(prev => [ ...prev, item ]);
    showToast("Adicionado ao bilhete", "success");
  };

  // refresh after placing bets (re-fetch bets and optionally update UI)
  const handleRefresh = () => {
    loadBets();
    // também atualiza saldo via localStorage — Header escuta localStorage
  };

  return (
    <div className="bets-page-container" style={{ display:"flex", gap:20, alignItems:"flex-start", padding:20 }}>
      <div style={{ flex: 1 }}>
        <h1 className="bets-title">Apostas Disponíveis</h1>

        {loading && <p>Carregando...</p>}

        {!loading && bets.length === 0 && (
          <p className="empty">Nenhuma aposta disponível no momento.</p>
        )}

        <div className="bets-list">
          {bets.map((bet) => (
            <div key={bet._id} className="bet-card">
              <div className="bet-header">
                <h2>{bet.title}</h2>
                <p className="subtitle">{bet.description || "Motivo do término"}</p>
              </div>

              <div className="bet-options">
                {bet.options.map((opt) => (
                  <div key={opt.id} className="option-item">
                    <div className="label">{opt.label}</div>

                    <div style={{ display:"flex", gap:8, alignItems:"center" }}>
                      <div className="odd-txt">{opt.odds.toFixed(2)}</div>
                      <button className="btn-ghost" onClick={() => addToSlip(bet, opt)}>Adicionar</button>
                    </div>
                  </div>
                ))}
              </div>

              <div className="footer">
                <p>Fecha em: <span>{new Date(bet.endsAt).toLocaleString()}</span></p>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* SLIP (sidebar em desktop / abaixo em mobile) */}
      <aside style={{ width: 360 }}>
        <BetSlip slip={slip} setSlip={setSlip} onRefresh={handleRefresh} />
      </aside>
    </div>
  );
}
