import React, { useState } from "react";
import Sidebar from "../components/Sidebar";
import "../styles/Dashboard.css";
import betsData from "../data/betsData";
import CouplesSelector from "../components/CouplesSelector";
import MainBetsBox from "../components/MainBetsBox";
import OtherBetsBox from "../components/OtherBetsBox";
import MenuApostas from "../components/MenuApostas";
import BetSlip from "../components/BetSlip";

/*
  Dashboard atualizado para:
  - renderizar Sidebar na coluna 1
  - usar estrutura compatível com Dashboard.css que você enviou
  - implementar handlers para adicionar ao slip, alterar valores, remover e apostar
*/

export default function Dashboard({ user = {}, onNavigate }) {
  const [selectedCouple, setSelectedCouple] = useState(betsData.couples[0].id);
  const [slip, setSlip] = useState([]);
  const [view, setView] = useState(null);

  // Adiciona item principal ao slip
  function handleSelectMain(item) {
    setSlip(prev => {
      const already = prev.find(p => p.id === item.id);
      if (already) return prev;
      return [
        ...prev,
        {
          id: item.id,
          label: item.label,
          odds: item.odds,
          oddLabel: "Principal",
          amount: 0
        }
      ];
    });
  }

  // Adiciona item de outras categorias ao slip
  function handlePickOther(item, cat) {
    setSlip(prev => {
      const already = prev.find(p => p.id === item.id);
      if (already) return prev;
      return [
        ...prev,
        {
          id: item.id,
          label: `${cat.category} — ${item.label}`,
          odds: item.odds,
          oddLabel: cat.category,
          amount: 0
        }
      ];
    });
  }

  function handleChangeAmount(idx, value) {
    setSlip(prev => {
      const copy = [...prev];
      copy[idx] = { ...copy[idx], amount: isNaN(value) ? 0 : value };
      return copy;
    });
  }

  function handleRemove(idx) {
    setSlip(prev => prev.filter((_, i) => i !== idx));
  }

  async function handlePlace(idx) {
    const item = slip[idx];
    if (!item) return;
    if (!item.amount || item.amount <= 0) {
      alert("Coloque um valor válido");
      return;
    }

    // Aqui você pode chamar a API placeWager para registrar a aposta no backend
    // Exemplo (quando tiver api): await placeWager(betId, { optionId: item.id, amount: item.amount })
    alert(
      `Apostado ${item.amount} pts em ${item.label} (odds ${item.odds}) — retorno possível ${(item.amount * item.odds).toFixed(2)}`
    );

    // opcional: atualizar saldo do user localmente (se quiser simular)
    // Remover a seleção confirmada do slip
    setSlip(prev => prev.filter((_, i) => i !== idx));
  }

  // total possível das seleções (somatório)
  const totalReturn = slip.reduce((s, it) => s + (Number(it.amount || 0) * Number(it.odds || 0)), 0);

  return (
    <div className="dashboard-root">
      {/* Sidebar (coluna 1) */}
      <Sidebar page={"dashboard"} onNavigate={onNavigate} />

      {/* Topbar (ocupa col 2-3 no grid) */}
      <div className="topbar" style={{ gridColumn: "2 / 4" }}>
        <div style={{ display: "flex", alignItems: "center", gap: 12 }}>
          <div className="brand" onClick={() => onNavigate("dashboard")}>
            BET DO TÉRMINO
          </div>

          <CouplesSelector
            couples={betsData.couples}
            value={selectedCouple}
            onChange={setSelectedCouple}
          />
        </div>

        <div style={{ display: "flex", gap: 12, alignItems: "center" }}>
          <div style={{ color: "#ccc" }}>
            Saldo:{" "}
            <strong style={{ color: "#ffcc00" }}>
              {user.balance || 0} pts
            </strong>
          </div>

          <button className="btn ghost" onClick={() => onNavigate("conta")}>
            Conta
          </button>
        </div>
      </div>

      {/* Content area (coluna 2) */}
      <div className="content-main" style={{ gridColumn: "2 / 3" }}>
        <div className="row">
          {/* Main bets column */}
          <div className="main-column">
            <h3>APOSTAS PRINCIPAIS</h3>
            {betsData.mainBets.map(i => (
              <div
                key={i.id}
                className="main-bet-row"
                onClick={() => handleSelectMain(i)}
                style={{ cursor: "pointer" }}
              >
                <div className="label">{i.label}</div>
                <div className="odds">{i.odds.toFixed(2)}</div>
              </div>
            ))}
          </div>

          {/* Other bets (wide) */}
          <div className="other-column" style={{ flex: 1 }}>
            <h3 style={{ color: "#ffcc00", marginTop: 0 }}>OUTRAS APOSTAS</h3>
            {betsData.otherBets.map(cat => (
              <div key={cat.category} className="other-category">
                <h4>{cat.category}</h4>
                {cat.items.map(it => (
                  <div
                    key={it.id}
                    className="other-item"
                    onClick={() => handlePickOther(it, cat)}
                    style={{ cursor: "pointer" }}
                  >
                    <div className="label">{it.label}</div>
                    <div className="odds">{it.odds.toFixed(2)}</div>
                  </div>
                ))}
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Right column (coluna 3) */}
      <div className="right-col" style={{ gridColumn: "3 / 4" }}>
        <MenuApostas onOpen={(v) => setView(v)} />

        <div className="widget">
          <h4>APOSTAS SELECIONADAS</h4>

          {/* Betslip: reuse seu componente BetSlip */}
          <div className="betslip-box">
            {slip.length === 0 && <div className="small-note">Nenhuma seleção</div>}

            {slip.map((s, idx) => (
              <div key={s.id} className="betslip-line" style={{ alignItems: "center" }}>
                <div style={{ flex: 1 }}>
                  <div style={{ fontWeight: 800 }}>{s.label}</div>
                  <div style={{ fontSize: 12, color: "#aaa" }}>{s.oddLabel} — {s.odds.toFixed(2)}</div>
                </div>

                <div style={{ display: "flex", flexDirection: "column", gap: 8, alignItems: "flex-end" }}>
                  <input
                    className="betslip-input"
                    type="number"
                    placeholder="Valor"
                    value={s.amount || ""}
                    onChange={(e) => handleChangeAmount(idx, Number(e.target.value))}
                  />
                  <div style={{ display: "flex", gap: 6 }}>
                    <button className="btn small" onClick={() => handlePlace(idx)}>Apostar</button>
                    <button className="btn ghost small" onClick={() => handleRemove(idx)}>Remover</button>
                  </div>
                </div>
              </div>
            ))}

            <div className="total-result">Possível retorno: {totalReturn.toFixed(2)}</div>
          </div>
        </div>
      </div>
    </div>
  );
}
