// src/pages/Dashboard.jsx
import React, { useState, useEffect } from "react";
import "../styles/Dashboard.css";
import betsData from "../data/betsData";
import CouplesSelector from "../components/CouplesSelector";
import OtherBetsBox from "../components/OtherBetsBox";

// Views (assume que InicioView, ApostasView, etc. existem)
// If you don't have other views, InicioView + ContaView below will work.
import InicioView from "./views/InicioView";
import ApostasView from "./views/ApostasView";
import ResultadosView from "./views/ResultadosView";
import TendenciasView from "./views/TendenciasView";
import HistoricoView from "./views/HistoricoView";
import ContaView from "./views/ContaView";

export default function Dashboard({ user = {}, onNavigate }) {
  const [view, setView] = useState("inicio"); // SPA internal route
  const [selectedCouple, setSelectedCouple] = useState(betsData.couples[0].id);
  const [slip, setSlip] = useState([]);
  const [theme, setTheme] = useState(() => localStorage.getItem("theme") || "light");

  useEffect(() => {
    document.body.classList.toggle("dark", theme === "dark");
    localStorage.setItem("theme", theme);
  }, [theme]);

  // Betslip handlers
  function addToSlip(item, labelPrefix = "Principal") {
    setSlip((p) => {
      if (p.find(x => x.id === item.id)) return p;
      return [...p, { id: item.id, label: item.label, odds: item.odds, oddLabel: labelPrefix, amount: 0 }];
    });
    // open right column by keeping view but user sees betslip always
  }

  function changeAmt(i, v) {
    setSlip(p => {
      const c = [...p]; c[i] = {...c[i], amount: Number(v) || 0 }; return c;
    });
  }

  function remove(i) {
    setSlip(p => p.filter((_, idx) => idx !== i));
  }

  function place(i) {
    const item = slip[i];
    if (!item || !item.amount || item.amount <= 0) return alert("Valor inv√°lido");
    const possible = item.amount * item.odds;
    alert(`Apostado ${formatBRL(item.amount)} em ${item.label} ‚Äî poss√≠vel retorno ${formatBRL(possible)}`);
    // simulated history / api call could be placed here
    setSlip(p => p.filter((_, idx) => idx !== i));
  }

  function handleLogout() {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    onNavigate("login");
  }

  const trends = betsData.mainBets.slice(0,6).map((b, idx) => ({
    label: b.label,
    score: Math.max(50 - idx * 6, 12)
  }));

  const Nav = () => (
    <div className="sidebar card" role="navigation">
      <div className="nav">
        <div className={`nav-item ${view === "inicio" ? "active" : ""}`} onClick={() => setView("inicio")}>üè† In√≠cio</div>
        <div className={`nav-item ${view === "apostas" ? "active" : ""}`} onClick={() => setView("apostas")}>üé≤ Apostas</div>
        <div className={`nav-item ${view === "resultados" ? "active" : ""}`} onClick={() => setView("resultados")}>üìà Resultados</div>
        <div className={`nav-item ${view === "regras" ? "active" : ""}`} onClick={() => setView("regras")}>üìò Regras</div>
        <div className={`nav-item ${view === "patro" ? "active" : ""}`} onClick={() => setView("patro")}>ü§ù Patroc√≠nios</div>
        <div className={`nav-item ${view === "historico" ? "active" : ""}`} onClick={() => setView("historico")}>üìú Hist√≥rico</div>
        <div className={`nav-item ${view === "conta" ? "active" : ""}`} onClick={() => setView("conta")}>üë§ Conta</div>
      </div>

      <div style={{height:12}} />

      <div className="widget-small card">
        <div className="label">‚ù§Ô∏è Casais em alta</div>
        <div className="value">3 casais</div>
      </div>

      <div style={{height:10}} />

      <div className="widget-small card">
        <div className="label">‚≠ê Apostas populares</div>
        <div className="value">Top 5</div>
      </div>
    </div>
  );

  // small helper to format BRL
  function formatBRL(n) {
    return Number(n).toLocaleString("pt-BR", { style: "currency", currency: "BRL" });
  }

  return (
    <div className="app-wrapper">
      <div className="dashboard-container">
        <div className="topbar">
          <div className="left">
            <div className="brand" onClick={() => setView("inicio")}>üéØ BET DO T√âRMINO</div>
            <CouplesSelector couples={betsData.couples} value={selectedCouple} onChange={setSelectedCouple} />
          </div>

          <div className="actions">
            <div className="balance">üíé Saldo: <strong>{formatBRL(user.balance || 0)}</strong></div>
            <button className="icon-btn" onClick={() => setTheme(t => t === "dark" ? "light" : "dark")}>
              {theme === "dark" ? "‚òÄÔ∏è Light" : "üåô Dark"}
            </button>
            <button className="icon-btn" onClick={handleLogout}>üö™ Sair</button>
          </div>
        </div>

        <div className="main-grid">
          <Nav />

          <div className="center-column">
            {view === "inicio" && <InicioView addToSlip={addToSlip} selectedCouple={selectedCouple} />}
            {view === "apostas" && <ApostasView categories={betsData.otherBets} addToSlip={addToSlip} />}
            {view === "resultados" && <ResultadosView />}
            {view === "regras" && <div className="card"><h3>üìò Regras</h3><p className="small-note">Regras do site (coloque aqui)</p></div>}
            {view === "patro" && <div className="card"><h3>ü§ù Patroc√≠nios</h3><p className="small-note">Seus patrocinadores aparecem aqui.</p></div>}
            {view === "historico" && <HistoricoView />}
            {view === "conta" && <ContaView user={user} onNavigate={setView} />}
          </div>

          <div className="right-column">
            <div className="betslip card">
              <h4>üßæ Apostas Selecionadas</h4>
              {slip.length === 0 && <div className="small-note">Nenhuma aposta selecionada</div>}
              {slip.map((s, idx) => (
                <div className="line" key={s.id}>
                  <div className="label">
                    {s.label}
                    <div style={{fontSize:12,color:'var(--muted)'}}>({s.oddLabel})</div>
                  </div>
                  <div className="controls">
                    <input
                      className="input-amt"
                      type="number"
                      placeholder="Valor (R$)"
                      value={s.amount || ''}
                      onChange={e => changeAmt(idx, e.target.value)}
                    />
                    <button className="btn" onClick={() => place(idx)}>Apostar</button>
                    <button className="btn ghost" onClick={() => remove(idx)}>Remover</button>
                  </div>
                </div>
              ))}
              <div style={{marginTop:10, fontWeight:800, color:'var(--muted)'}}>Poss√≠vel retorno: <span style={{color:'var(--accent)'}}>
                {formatBRL(slip.reduce((acc, it) => acc + (it.amount||0)*it.odds, 0))}
              </span></div>
            </div>

            <div className="trends card">
              <h3>üìä Tend√™ncias</h3>
              <div style={{height:8}}/>
              {trends.map((t, i) => (
                <div className="trend-row" key={i}>
                  <div className="t-label">{t.label}</div>
                  <div className="t-score">{t.score}%</div>
                </div>
              ))}
              <div style={{height:8}} />
              <div className="small-note">Dados simulados ‚Ä¢ conecte backend para reais</div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
