// ==============================
// src/pages/Dashboard.jsx
// ==============================
import React, { useState } from "react";
import Sidebar from "../components/Sidebar";
import "../styles/Dashboard.css";
import betsData from "../data/betsData";
import CouplesSelector from "../components/CouplesSelector";
import MainBetsBox from "../components/MainBetsBox";
import OtherBetsBox from "../components/OtherBetsBox";
import MenuApostas from "../components/MenuApostas";
import BetSlip from "../components/BetSlip";

export default function Dashboard({ user = {}, onNavigate }) {
  const [selectedCouple, setSelectedCouple] = useState(betsData.couples[0].id);
  const [slip, setSlip] = useState([]);

  function handleSelectMain(item) {
    setSlip((prev) => {
      const exists = prev.find((p) => p.id === item.id);
      if (exists) return prev;
      return [
        ...prev,
        {
          id: item.id,
          label: item.label,
          odds: item.odds,
          oddLabel: "Principal",
          amount: 0,
        },
      ];
    });
  }

  function handlePickOther(item, cat) {
    setSlip((prev) => {
      const exists = prev.find((p) => p.id === item.id);
      if (exists) return prev;
      return [
        ...prev,
        {
          id: item.id,
          label: `${cat.category} — ${item.label}`,
          odds: item.odds,
          oddLabel: cat.category,
          amount: 0,
        },
      ];
    });
  }

  function handleChangeAmount(idx, value) {
    setSlip((prev) => {
      const copy = [...prev];
      copy[idx] = {
        ...copy[idx],
        amount: isNaN(value) ? 0 : value,
      };
      return copy;
    });
  }

  function handleRemove(idx) {
    setSlip((prev) => prev.filter((_, i) => i !== idx));
  }

  function handlePlace(idx) {
    const item = slip[idx];
    if (!item.amount || item.amount <= 0)
      return alert("Digite um valor válido");

    alert(
      `Apostado ${item.amount} pts em ${item.label} (odds ${item.odds}) → retorno possível R$ ${(item.amount * item.odds).toFixed(
        2
      )}`
    );

    setSlip((prev) => prev.filter((_, i) => i !== idx));
  }

  const totalReturn = slip.reduce(
    (sum, s) => sum + s.amount * s.odds,
    0
  );

  return (
    <div className="dashboard-root">
      {/* Sidebar */}
      <Sidebar page={"inicio"} onNavigate={onNavigate} />

      {/* TOPBAR */}
      <div className="topbar">
        <div style={{ display: "flex", alignItems: "center", gap: 12 }}>
          <div className="brand" onClick={() => onNavigate("inicio")}>
            BET DO TÉRMINO
          </div>

          <CouplesSelector
            couples={betsData.couples}
            value={selectedCouple}
            onChange={setSelectedCouple}
          />
        </div>

        <div style={{ display: "flex", gap: 12, alignItems: "center" }}>
          <div style={{ color: "#ccc" }}>
            Saldo:{" "}
            <strong style={{ color: "#ffcc00" }}>
              {user.balance || 0} pts
            </strong>
          </div>

          <button className="btn ghost" onClick={() => onNavigate("conta")}>
            Conta
          </button>
        </div>
      </div>

      {/* COLUNA PRINCIPAL */}
      <div className="content-main">
        <div className="row">
          
          {/* APOSTAS PRINCIPAIS */}
          <div className="main-column">
            <h3>APOSTAS PRINCIPAIS</h3>

            {betsData.mainBets.map((i) => (
              <div
                key={i.id}
                className="main-bet-row"
                onClick={() => handleSelectMain(i)}
              >
                <div className="label">{i.label}</div>
                <div className="odds">{i.odds.toFixed(2)}</div>
              </div>
            ))}
          </div>

          {/* OUTRAS APOSTAS */}
          <div className="other-column">
            <h3 style={{ color: "#ffcc00" }}>OUTRAS APOSTAS</h3>

            {betsData.otherBets.map((cat) => (
              <div key={cat.category} className="other-category">
                <h4>{cat.category}</h4>

                {cat.items.map((it) => (
                  <div
                    key={it.id}
                    className="other-item"
                    onClick={() => handlePickOther(it, cat)}
                  >
                    <div className="label">{it.label}</div>
                    <div className="odds">{it.odds.toFixed(2)}</div>
                  </div>
                ))}
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* COLUNA DIREITA */}
      <div className="right-col">
        <MenuApostas onOpen={() => {}} />

        <div className="widget">
          <h4>APOSTAS SELECIONADAS</h4>

          <div className="betslip-box">
            {slip.length === 0 && (
              <div className="small-note">Nenhuma aposta selecionada</div>
            )}

            {slip.map((s, idx) => (
              <div key={s.id} className="betslip-line">
                <div style={{ flex: 1 }}>
                  <div style={{ fontWeight: 800 }}>{s.label}</div>
                  <div style={{ fontSize: 12, color: "#aaa" }}>
                    {s.oddLabel} — {s.odds.toFixed(2)}
                  </div>
                </div>

                <div style={{ display: "flex", flexDirection: "column", gap: 8 }}>
                  <input
                    className="betslip-input"
                    type="number"
                    placeholder="Valor"
                    value={s.amount || ""}
                    onChange={(e) =>
                      handleChangeAmount(idx, Number(e.target.value))
                    }
                  />

                  <div style={{ display: "flex", gap: 6 }}>
                    <button className="btn small" onClick={() => handlePlace(idx)}>
                      Apostar
                    </button>
                    <button
                      className="btn ghost small"
                      onClick={() => handleRemove(idx)}
                    >
                      Remover
                    </button>
                  </div>
                </div>
              </div>
            ))}

            <div className="total-result">
              Possível retorno: {totalReturn.toFixed(2)}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
