import React, { useEffect, useState } from "react";
import api from "../api/api";
import "../styles/Dashboard.css";
import showToast from "../utils/showToast";
import { Link } from "react-router-dom";

export default function Dashboard() {
  const [saldo, setSaldo] = useState(0);

  // Exemplo de saldo ‚Äî futuramente conectamos ao backend
  useEffect(() => {
    const fakeBalance = 125.40; // Exemplo tempor√°rio
    setSaldo(fakeBalance);
  }, []);

  // Lista fixa do casal
  const casal = {
    nomeA: "Alef",
    nomeB: "Larissa",
    fotoA: "/img/alef.jpg",     // personalize depois
    fotoB: "/img/larissa.jpg"   // personalize depois
  };

  // ‚ÄúApostas em alta‚Äù ‚Äî 5 motivos com odds
  const apostasAlta = [
    { motivo: "Falta de comunica√ß√£o", odd: 1.85 },
    { motivo: "Trai√ß√£o / quebra de confian√ßa", odd: 2.40 },
    { motivo: "Diferen√ßas de objetivos", odd: 3.10 },
    { motivo: "Conflitos frequentes", odd: 2.75 },
    { motivo: "Desinteresse / esfriamento", odd: 2.20 }
  ];

  return (
    <div className="dashboard fade-in">

      {/* ====================== HEADER COM SALDO ====================== */}
      <div className="top-bar">
        <h1>Alef & Larissa ‚ù§Ô∏è</h1>
        <div className="saldo">
          Saldo: <span>R$ {saldo.toFixed(2)}</span>
        </div>
      </div>

      {/* ====================== APOSTAS EM ALTA ====================== */}
      <h2 className="sec-title">üî• Apostas em Alta</h2>

      <div className="apostas-alta">
        {apostasAlta.map((item, index) => (
          <div key={index} className="alta-card">
            <p>{item.motivo}</p>
            <span className="odd">Odd {item.odd}</span>
          </div>
        ))}
      </div>

      {/* ====================== BOT√ïES ====================== */}
      <div className="dash-buttons">
        <Link to="/apostas" className="dash-btn">Ver todas as apostas</Link>
        <Link to="/conta" className="dash-btn secondary">Minha conta</Link>
      </div>
    </div>
  );
}
