import React, { useState } from "react";
import "../styles/Dashboard.css";

import betsData from "../data/betsData";
import CouplesSelector from "../components/CouplesSelector";
import MainBetsBox from "../components/MainBetsBox";
import OtherBetsBox from "../components/OtherBetsBox";
import MenuApostas from "../components/MenuApostas";
import MenuConta from "../components/MenuConta";
import BetSlip from "../components/BetSlip";

/*
  Props expected:
  - user (object) with username and balance
  - onNavigate function to change pages
  - onRefresh (optional)
*/
export default function Dashboard({ user = {}, onNavigate }) {
  const [selectedCouple, setSelectedCouple] = useState(betsData.couples[0].id);
  const [slip, setSlip] = useState([]);
  const [view, setView] = useState(null); // deposit, withdraw, history, bets

  function handleSelectMain(item) {
    // add to slip
    setSlip(prev => {
      const already = prev.find(p => p.id === item.id);
      if (already) return prev;
      return [...prev, { id: item.id, label: item.label, odds: item.odds, oddLabel: "Principal", amount: 0 }];
    });
  }

  function handlePickOther(item, cat) {
    setSlip(prev => {
      const already = prev.find(p => p.id === item.id);
      if (already) return prev;
      return [...prev, { id: item.id, label: `${cat.category} — ${item.label}`, odds: item.odds, oddLabel: cat.category, amount: 0 }];
    });
  }

  function handleChangeAmount(idx, value) {
    setSlip(prev => {
      const copy = [...prev];
      copy[idx] = { ...copy[idx], amount: isNaN(value) ? 0 : value };
      return copy;
    });
  }

  function handleRemove(idx) {
    setSlip(prev => prev.filter((_, i) => i !== idx));
  }

  async function handlePlace(idx) {
    // TODO: call API placeWager per item or aggregated — for now we just simulate
    const item = slip[idx];
    if (!item.amount || item.amount <= 0) return alert("Coloque um valor válido");
    alert(`Apostado ${item.amount} pts em ${item.label} (odds ${item.odds}) — retorno possível ${(item.amount * item.odds).toFixed(2)}`);
    // clear after place
    setSlip(prev => prev.filter((_, i) => i !== idx));
  }

  return (
    <div className="dashboard-root">
      <div className="topbar">
        <div style={{ display:"flex", alignItems:"center", gap:12 }}>
          <div className="brand" onClick={() => window.location.reload()}>BET DO TÉRMINO</div>
          <CouplesSelector couples={betsData.couples} value={selectedCouple} onChange={setSelectedCouple} />
        </div>

        <div style={{ display:"flex", gap:12, alignItems:"center" }}>
          <div style={{ color:"#ccc" }}>Saldo: <strong style={{ color:"#ffcc00" }}>{user.balance || 0} pts</strong></div>
          <button className="btn ghost" onClick={() => onNavigate("profile")}>Conta</button>
        </div>
      </div>

      <div className="left-col">
        <MainBetsBox items={betsData.mainBets} onSelect={handleSelectMain} />
      </div>

      <div className="mid-col">
        <div className="box">
          <h3>OUTRAS APOSTAS</h3>
          <div className="search-bar">
            {/* future search */}
          </div>
          <OtherBetsBox categories={betsData.otherBets} onPick={handlePickOther} />
        </div>
      </div>

      <div className="right-col">
        <MenuApostas onOpen={(v) => setView(v)} />
        <MenuConta user={user} onOpen={(v) => setView(v)} />
        <div className="box">
          <h3>APOSTAS SELECIONADAS</h3>
          <BetSlip slip={slip} onChangeAmount={handleChangeAmount} onRemove={handleRemove} onPlace={handlePlace} />
        </div>
      </div>
    </div>
  );
}
