import React from "react";
import "./bets.css";
import showToast from "../utils/showToast";

// Função para gerar odds aleatórias realistas
const randomOdd = () => (Math.random() * (3.00 - 1.20) + 1.20).toFixed(2);

// TODAS AS CATEGORIAS E OPÇÕES
const betCategories = [
  {
    title: "1. Quem Termina",
    options: [
      "Parceiro(a) A",
      "Parceiro(a) B",
      "Ambos de forma mútua",
    ],
  },
  {
    title: "2. Duração do Relacionamento",
    options: [
      "Curto prazo (< 6 meses)",
      "Médio prazo (6 meses – 2 anos)",
      "Longo prazo (> 2 anos)",
    ],
  },
  {
    title: "3. Motivo do Término",
    options: [
      "Falta de comunicação",
      "Falta de confiança / traição",
      "Diferenças de objetivos de vida",
      "Problemas financeiros",
      "Distância / mudança de cidade ou país",
      "Desinteresse ou incompatibilidade",
      "Conflitos frequentes / brigas constantes",
      "Crescimento pessoal / mudança de prioridades",
      "Término impulsivo ou temporário",
    ],
  },
  {
    title: "4. Natureza do Término",
    options: [
      "Amigável / sem ressentimentos",
      "Conturbado / com brigas",
      "Silencioso / sem comunicação",
      "Dramático / com grandes conflitos",
      "Repentino / sem aviso",
    ],
  },
  {
    title: "5. Forma de Comunicação",
    options: [
      "Pessoalmente",
      "Mensagem / WhatsApp",
      "Ligação telefônica",
      "Redes sociais (unfollow/bloqueio)",
      "Por terceiros / indiretas",
    ],
  },
  {
    title: "6. Intensidade Emocional",
    options: [
      "Alívio / sensação de liberdade",
      "Tristeza profunda / luto",
      "Raiva / frustração",
      "Indiferença",
      "Confusão / sentimentos mistos",
    ],
  },
  {
    title: "7. Frequência de Discussões Antes do Término",
    options: [
      "Constante",
      "Esporádica",
      "Quase inexistente",
      "Aumento recente de conflitos",
    ],
  },
  {
    title: "8. Fatores Externos",
    options: [
      "Interferência de amigos ou familiares",
      "Diferenças de vida ou carreira",
      "Problemas de confiança ou traição",
      "Situações financeiras",
      "Mudança de cidade ou país",
    ],
  },
  {
    title: "9. Possibilidade de Reconciliação",
    options: [
      "Pouca chance",
      "Média chance",
      "Alta chance / já houve pausas antes",
    ],
  },
];

export default function Bets() {
  return (
    <div className="bets-page fade-in">

      <h1 className="bets-title">Possíveis Términos</h1>

      {betCategories.map((cat, index) => (
        <div key={index} className="bet-card">
          <h2 className="category-title">{cat.title}</h2>

          <div className="bet-options">
            {cat.options.map((opt, idx) => (
              <div key={idx} className="option-item">
                <div className="label">{opt}</div>

                <button
                  className="odd-btn"
                  onClick={() => showToast("Slip de aposta será adicionado em breve!", "info")}
                >
                  {randomOdd()}
                </button>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}
