// src/components/OtherBetsBox.jsx
import React, { useState } from "react";
import "../styles/Dashboard.css";

export default function OtherBetsBox({ categories, onPick }) {
  const [openCategory, setOpenCategory] = useState(null);

  function toggle(catName) {
    setOpenCategory((prev) => (prev === catName ? null : catName));
  }

  const emojiFor = (name) => {
    if (/termino|motivo|motivos/i.test(name)) return "üíî";
    if (/quem termina/i.test(name)) return "‚û°Ô∏è";
    if (/dura√ß√£o|prazo/i.test(name)) return "‚è≥";
    if (/forma|comunica√ß√£o/i.test(name)) return "üì±";
    if (/intensidade|emo√ß√£o/i.test(name)) return "üî•";
    if (/fatores|externos/i.test(name)) return "üåç";
    return "üîπ";
  };

  return (
    <div className="other-bets-container">
      {categories.map((cat) => (
        <div key={cat.category} className="other-category-block">
          <div
            className="other-category-header"
            onClick={() => toggle(cat.category)}
            role="button"
            tabIndex={0}
            onKeyDown={(e) => { if (e.key === "Enter") toggle(cat.category); }}
          >
            <div style={{ display: "flex", alignItems: "center", gap: 8 }}>
              <span className="cat-emoji">{emojiFor(cat.category)}</span>
              <span>{cat.category}</span>
            </div>
            <div style={{ color: "var(--muted)", fontWeight: 800 }}>{openCategory === cat.category ? "‚ñ≤" : "‚ñº"}</div>
          </div>

          <div className={`other-bets-list ${openCategory === cat.category ? "open" : ""}`}>
            {cat.items.map((it) => (
              <div key={it.id} className="other-bet-item" onClick={() => onPick(it, cat)}>
                <div className="label">{it.label}</div>
                <div className="odds">{it.odds.toFixed(2)}</div>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}
